import React from "react";
import { MantineProvider, Avatar } from "@mantine/core";
import { BiReset } from "react-icons/bi";
import { FaUpload } from "react-icons/fa6";
import { useState } from "react";

export default function Newempform() {
    
  const [selectedImage, setSelectedImage] = useState("");

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleResetImage = () => {
    setSelectedImage("");
  };

  return (
    <div className="bg-white p-4 rounded-md h-screen">
      <div>
        <div>
          <h4 className="font-bold">Add Profile Picture</h4>
          <div className="flex  items-center md:items-center mt-4 gap-4">
            <div>
              <MantineProvider>
                <Avatar
                  className="w-28 md:w-20  bg-[#EBE9F6] p-2 rounded-md"
                  src=""
                  alt="it's me"
                />
              </MantineProvider>
            </div>
            <div className="flex items-center">
              <div justify="center" className="flex flex-col md:flex-row">
                <div className="bg-[#5336FD] px-3 py-2 flex text-white rounded-md cursor-pointer items-center hover:scale-[1.020] duration-150">
                  <FaUpload size={18} />
                  <h1 className="ml-2 text-xs font-bold">Upload Avatar</h1>
                </div>

                <div className="bg-[#EBE9F6] px-3 py-2 flex rounded-md md:ml-4 mt-4 md:mt-0 cursor-pointer items-center hover:scale-[1.020] duration-150 ">
                  <BiReset size={20} />
                  <h1 className="ml-2 text-xs font-bold">Reset Image</h1>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
