import React, { useState } from "react";
import { useParams } from "react-router-dom";
import MenuTabs from "./Menutabs";

export default function EditEmployee() {
  const { empid, ename, designation, jdate, status } = useParams();

  // State variables to store the edited values
  const [editedName, setEditedName] = useState(ename);
  const [editedDesignation, setEditedDesignation] = useState(designation);
  const [editedJoiningDate, setEditedJoiningDate] = useState(jdate);
  const [editedStatus, setEditedStatus] = useState(status);

  // Function to handle saving the edited information
  const handleSave = () => {
    // Here you can implement the logic to save the edited data
    // For demonstration purposes, I'm just logging the edited values
    console.log("Edited Name:", editedName);
    console.log("Edited Designation:", editedDesignation);
    console.log("Edited Joining Date:", editedJoiningDate);
    console.log("Edited Status:", editedStatus);
  };

  return (
    <div>
      <MenuTabs />
      <h1>Edit Employee</h1>
      <p>Employee ID: {empid}</p>
      <div>
        <label>
          Name:
          <input
            type="text"
            value={editedName}
            onChange={(e) => setEditedName(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Designation:
          <input
            type="text"
            value={editedDesignation}
            onChange={(e) => setEditedDesignation(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Joining Date:
          <input
            type="text"
            value={editedJoiningDate}
            onChange={(e) => setEditedJoiningDate(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Status:
          <select
            value={editedStatus}
            onChange={(e) => setEditedStatus(e.target.value)}
          >
            <option value={active}>Active</option>
            <option value={inactive}>Inactive</option>
            <option value={removed}>Removed</option>
          </select>
        </label>
      </div>
      <button onClick={handleSave}>Save</button>
    </div>
  );
}
