import React from "react";
import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { DemoContainer } from "@mui/x-date-pickers/internals/demo";
import ArrowDropDownRoundedIcon from "@mui/icons-material/ArrowDropDownRounded";
import PopupMessage from "./PopupMessage";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { MantineProvider, Avatar } from "@mantine/core";
import { BiReset } from "react-icons/bi";
import { FaUpload } from "react-icons/fa6";
import { makeStyles } from "@mui/styles";
import classNames from "classnames";
import { createGlobalStyle } from "styled-components";

const GlobalStyles = createGlobalStyle`
  .MuiMenuItem-root {
    font-family: Euclid;
    font-size: 14px;
    font-weight: bold;
  }
`;

const useStyles = makeStyles({
  root: {
    "& .MuiInputLabel-root": {
      fontFamily: "euclid",
      fontSize: 14,
      paddingTop: -2.5,
      fontWeight: "bold",
    },
    "& .MuiInputLabel-root.Mui-focused": {
      color: "black",
      fontWeight: "bold",
      fontSize: 15,
    },
    "& .MuiInputBase-root": {
      backgroundColor: "#f0f9ff",
      border: "0 none",
      borderRadius: 7,
      height: 50,
      width: "100%",
      overflow: "hidden",
    },
    "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent",
    },
    "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent",
    },
    "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline": {
      borderColor: "gray",
    },
    "& .Muilplaceholder": {
      fontFamily: "euclid",
      fontSize: 10,
    },
    "& .MuiOutlinedInput-input": {
      fontFamily: "euclid-medium",
      fontSize: 14,
    },
    "& .MuiOutlinedInput-input": {
      fontFamily: "euclid-medium",
      fontSize: 14,
    },
    "& .MuiMenuItem-root": {
      fontFamily: "euclid-medium",
      fontSize: 10,
    },
    "& ::placeholder": {
      fontSize: 12,
    },
    display: "block",
    width: "100%",
  },
});

export function PersonalInformation({ onNext }) {
  const [selectedImage, setSelectedImage] = useState("");
  const classes = useStyles();

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleResetImage = () => {
    setSelectedImage("");
  };
  return (
    <div className="">
      <div>
        <h4 className="font-bold">Personal Information</h4>
        <div className="flex items-center md:items-center mt-4 gap-4">
          <div>
            <MantineProvider>
              <Avatar
                className="w-28 md:w-20 bg-sky-50 p-2 rounded-md"
                src={selectedImage || ""}
                alt="Profile"
              />
            </MantineProvider>
          </div>
          <div className="flex items-center">
            <div className="flex flex-col md:flex-row">
              <label
                htmlFor="upload-avatar"
                className="bg-[#5336FD] px-3 py-2 flex text-white rounded-md cursor-pointer items-center hover:scale-[1.020] duration-150"
              >
                <input
                  type="file"
                  id="upload-avatar"
                  accept="image/*"
                  onChange={handleImageUpload}
                  style={{ display: "none" }}
                />
                <FaUpload size={18} />
                <h1 className="ml-2 text-xs font-bold">Upload Avatar</h1>
              </label>
              <div
                className="bg-sky-50 px-3 py-2 flex rounded-md md:ml-4 mt-4 md:mt-0 cursor-pointer items-center hover:scale-[1.020] duration-150"
                onClick={handleResetImage}
              >
                <BiReset size={20} />
                <h1 className="ml-2 text-xs font-bold">Reset Image</h1>
              </div>
            </div>
          </div>
        </div>
        <div className="mt-5">
          <div className="flex gap-5 flex-wrap sm:flex-nowrap">
            <div className="w-full lg:w-1/3 flex flex-col">
              <TextField
                className={classNames(
                  "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                  classes.root
                )}
                id="fname"
                name="fname"
                label="First Name"
                variant="outlined"
                margin="dense"
              />
            </div>
            <div className="w-full lg:w-1/3 flex flex-col">
              <TextField
                className={classNames(
                  "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                  classes.root
                )}
                id="lname"
                name="lname"
                label="Last Name"
                variant="outlined"
                margin="dense"
              />
            </div>
            <div className="w-full lg:w-1/3 flex flex-col">
              <TextField
                className={classNames(
                  "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                  classes.root
                )}
                id="empid"
                name="empid"
                label="Employee Id"
                variant="outlined"
                margin="dense"
              />
            </div>
          </div>
          <div className="flex gap-5 flex-wrap sm:flex-nowrap mt-5">
            <div className="w-full lg:w-1/3 flex flex-col">
              <LocalizationProvider
                dateAdapter={AdapterDayjs}
                className="w-full"
              >
                <DemoContainer components={["DatePicker"]}>
                  <DatePicker
                    label="Date of Birth"
                    className={classNames(
                      "col-span-12 sm:col-span-6 xl:col-span-2",
                      classes.root
                    )}
                  />
                </DemoContainer>
              </LocalizationProvider>
            </div>

            <div className="w-full lg:w-1/3 flex flex-col">
              <FormControl
                variant="outlined"
                margin="dense"
                className={classNames(
                  "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                  classes.root
                )}
              >
                <InputLabel id="gender-label" className="w-52">
                  Gender
                </InputLabel>
                <Select
                  labelId="gender-label"
                  id="gender"
                  name="gender"
                  label="Gender"
                  IconComponent={(props) => (
                    <ArrowDropDownRoundedIcon
                      {...props}
                      sx={{ fontSize: 40 }}
                    />
                  )}
                >
                  <GlobalStyles />
                  <MenuItem
                    value="male"
                    // sx={{
                    //   fontFamily: "Euclid",
                    //   fontSize: 14,
                    //   fontWeight: "bold",
                    // }}
                  >
                    Male
                  </MenuItem>
                  <MenuItem value="female">Female</MenuItem>
                </Select>
              </FormControl>
            </div>
            <div className="w-full lg:w-1/3 flex flex-col">
              <FormControl
                variant="outlined"
                margin="dense"
                className={classNames(
                  "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                  classes.root
                )}
              >
                <InputLabel id="maritialstatus-label" className="w-52">
                  Maritial Status
                </InputLabel>
                <Select
                  labelId="maritialstatus-label"
                  id="maritialstatus"
                  name="maritialstatus"
                  label="Maritial Status"
                  IconComponent={(props) => (
                    <ArrowDropDownRoundedIcon
                      {...props}
                      sx={{ fontSize: 40 }}
                    />
                  )}
                >
                  <MenuItem value="single">Single</MenuItem>
                  <MenuItem value="married">Married</MenuItem>
                  <MenuItem value="divorced">Divorced</MenuItem>
                  <MenuItem value="widowed">Widowed</MenuItem>
                </Select>
              </FormControl>
            </div>
          </div>
        </div>
      </div>
      <div className="w-full flex justify-end md:absolute md:z-0 md:bottom-36 md:right-10">
        <button
          type="button"
          onClick={onNext}
          className="bg-[#5336FD] text-white px-5 py-2 mt-8 rounded-md hover:scale-[1.020]"
        >
          Next
        </button>
      </div>
    </div>
  );
}

export function EmploymentInformation({ onPrev, onNext }) {
  const classes = useStyles();

  // Employment Information Form Component
  return (
    <div className="">
      <h4 className="font-bold">Employment Information</h4>
      <div className="mt-2">
        <div className="flex gap-5 flex-wrap sm:flex-nowrap">
          <div className="w-full lg:w-1/3 flex flex-col">
            <LocalizationProvider dateAdapter={AdapterDayjs} className="w-full">
              <DemoContainer components={["DatePicker"]}>
                <DatePicker
                  label="Date of Joining"
                  className={classNames(
                    "col-span-12 sm:col-span-6 xl:col-span-2",
                    classes.root
                  )}
                />
              </DemoContainer>
            </LocalizationProvider>
          </div>

          <div className="w-full lg:w-1/3 flex flex-col">
            <FormControl
              variant="outlined"
              margin="dense"
              className={classNames(
                "col-span-12 sm:col-span-6 xl:col-span-2",
                classes.root
              )}
            >
              <InputLabel id="dept-label" className="w-52">
                Department
              </InputLabel>
              <Select
                labelId="dept-label"
                id="gender"
                name="gender"
                label="Gender"
                IconComponent={(props) => (
                  <ArrowDropDownRoundedIcon {...props} sx={{ fontSize: 40 }} />
                )}
              >
                <MenuItem value="informationtechnology">
                  Information Technology
                </MenuItem>
                <MenuItem value="operations">Operations</MenuItem>
                <MenuItem value="sales">Sales</MenuItem>
                <MenuItem value="humanresources">Human Resources</MenuItem>
                <MenuItem value="finance">Finance</MenuItem>
                <MenuItem value="marketing">Marketing</MenuItem>
              </Select>
            </FormControl>
          </div>

          <div className="w-full lg:w-1/3 flex flex-col">
            <FormControl
              variant="outlined"
              margin="dense"
              className={classNames(
                "col-span-12 sm:col-span-6 xl:col-span-2",
                classes.root
              )}
            >
              <InputLabel id="report-label" className="w-52">
                Reporting To
              </InputLabel>
              <Select
                labelId="report-label"
                id="reporting"
                name="reporting"
                label="Reporting To"
                IconComponent={(props) => (
                  <ArrowDropDownRoundedIcon {...props} sx={{ fontSize: 40 }} />
                )}
              >
                <MenuItem value="swpnilpatil">Swpnil Patil</MenuItem>
                <MenuItem value="sheetalpatil">Sheetal Patil</MenuItem>
                <MenuItem value="ishapathak">Isha Pathak</MenuItem>
                <MenuItem value="sushantkhadilkar">Sushant Khadilkar</MenuItem>
                <MenuItem value="shubhamshinde">Shubham Shinde</MenuItem>
              </Select>
            </FormControl>
          </div>
        </div>
        <div className="flex gap-5 flex-wrap sm:flex-nowrap mt-5">
          <div className="w-full lg:w-1/3 flex flex-col">
            <FormControl
              variant="outlined"
              margin="dense"
              className={classNames(
                "col-span-12 sm:col-span-6 xl:col-span-2",
                classes.root
              )}
            >
              <InputLabel id="tl-label" className="w-52">
                Team Leader
              </InputLabel>
              <Select
                labelId="tl-label"
                id="teamleader"
                name="teamleader"
                label="Team Leader"
                IconComponent={(props) => (
                  <ArrowDropDownRoundedIcon {...props} sx={{ fontSize: 40 }} />
                )}
              >
                <MenuItem value="swpnilpatil">Swpnil Patil</MenuItem>
                <MenuItem value="sheetalpatil">Sheetal Patil</MenuItem>
                <MenuItem value="ishapathak">Isha Pathak</MenuItem>
                <MenuItem value="sushantkhadilkar">Sushant Khadilkar</MenuItem>
                <MenuItem value="shubhamshinde">Shubham Shinde</MenuItem>
              </Select>
            </FormControl>
          </div>
          <div className="w-full lg:w-1/3 flex flex-col">
            <FormControl
              variant="outlined"
              margin="dense"
              className={classNames(
                "col-span-12 sm:col-span-6 xl:col-span-2",
                classes.root
              )}
            >
              <InputLabel id="report-label" className="w-52">
                Reporting To
              </InputLabel>
              <Select
                labelId="report-label"
                id="reporting"
                name="reporting"
                label="Reporting To"
                IconComponent={(props) => (
                  <ArrowDropDownRoundedIcon {...props} sx={{ fontSize: 40 }} />
                )}
              >
                <MenuItem value="swpnilpatil">Swpnil Patil</MenuItem>
                <MenuItem value="sheetalpatil">Sheetal Patil</MenuItem>
