import React, { useState, useEffect, useRef } from "react";
import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { DemoContainer } from "@mui/x-date-pickers/internals/demo";
import ArrowDropDownRoundedIcon from "@mui/icons-material/ArrowDropDownRounded";
import PopupMessage from "./PopupMessage";
import { useNavigate } from "react-router-dom";
import { MantineProvider, Avatar } from "@mantine/core";
import { BiReset } from "react-icons/bi";
import { FaUpload } from "react-icons/fa6";
import { makeStyles } from "@mui/styles";
import classNames from "classnames";
import { createGlobalStyle } from "styled-components";
import { IoAddCircle } from "react-icons/io5";
import { IoIosRemoveCircle } from "react-icons/io";
import { motion } from "framer-motion";

const GlobalStyles = createGlobalStyle`
  .MuiMenuItem-root {
    font-family: Euclid;
    font-size: 14px;
    font-weight: bold;
    margin: auto 8px;
    border-radius: 7px;
  }
  .MuiMenuItem-root:hover {
    background-color:#e0f2fe;
    padding-left: 15px;
  }
  .MuiMenuItem-root:hover {
    transition-duration: 0.2s;
  }

  ::-webkit-scrollbar {
    display: none;
    -ms-overflow-style: none;
    scrollbar-width: none;
}
`;

const useStyles = makeStyles({
  root: {
    "& .MuiInputLabel-root": {
      fontFamily: "euclid",
      fontSize: 14,
      paddingTop: -2.5,
      fontWeight: "bold",
    },
    "& .MuiInputLabel-root.Mui-focused": {
      color: "black",
      fontWeight: "bold",
      fontSize: 15,
    },
    "& .MuiInputBase-root": {
      backgroundColor: "#f0f9ff",
      border: "0 none",
      borderRadius: 7,
      height: 50,
      width: "100%",
      overflow: "hidden",
    },
    "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent",
    },
    "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent",
    },
    "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline": {
      borderColor: "gray",
    },
    "& .Muilplaceholder": {
      fontFamily: "euclid",
      fontSize: 10,
    },
    "& .MuiOutlinedInput-input": {
      fontFamily: "euclid-medium",
      fontSize: 14,
    },
    "& .MuiOutlinedInput-input": {
      fontFamily: "euclid-medium",
      fontSize: 14,
    },
    "& ::placeholder": {
      fontSize: 12,
    },
    display: "block",
    width: "100%",
  },
});

export function PersonalInformation({ onNext }) {
  const [selectedImage, setSelectedImage] = useState("");
  const classes = useStyles();

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleResetImage = () => {
    setSelectedImage("");
  };
  return (
    <div className="">
      <motion.div animate={{ x: 0 }} initial={{ x: 50 }}>
        <h4 className="font-bold">Personal Information</h4>
        <div className="flex items-center md:items-center mt-4 gap-4">
          <div>
            <MantineProvider>
              <Avatar
                className="w-28 md:w-20 bg-sky-50 p-2 rounded-md"
                src={selectedImage || ""}
                alt="Profile"
              />
            </MantineProvider>
          </div>
          <div className="flex items-center">
            <div className="flex flex-col md:flex-row">
              <label
                htmlFor="upload-avatar"
                className="bg-[#5336FD] px-3 py-2 flex text-white rounded-md cursor-pointer items-center hover:scale-[1.020] duration-150"
              >
                <input
                  type="file"
                  id="upload-avatar"
                  accept="image/*"
                  onChange={handleImageUpload}
                  style={{ display: "none" }}
                />
                <FaUpload size={18} />
                <h1 className="ml-2 text-xs font-bold">Upload Avatar</h1>
              </label>
              <div
                className="bg-sky-50 px-3 py-2 flex rounded-md md:ml-4 mt-4 md:mt-0 cursor-pointer items-center hover:scale-[1.020] duration-150"
                onClick={handleResetImage}
              >
                <BiReset size={20} />
                <h1 className="ml-2 text-xs font-bold">Reset Image</h1>
              </div>
            </div>
          </div>
        </div>
        <div className="mt-5">
          <div className="flex gap-5 flex-wrap sm:flex-nowrap">
            <div className="w-full lg:w-1/3 flex flex-col">
              <TextField
                className={classNames(
                  "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                  classes.root
                )}
                id="fname"
                name="fname"
                label="First Name"
                variant="outlined"
                margin="dense"
              />
            </div>
            <div className="w-full lg:w-1/3 flex flex-col">
              <TextField
                className={classNames(
                  "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                  classes.root
                )}
                id="lname"
                name="lname"
                label="Last Name"
                variant="outlined"
                margin="dense"
              />
            </div>
            <div className="w-full lg:w-1/3 flex flex-col">
              <TextField
                className={classNames(
                  "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                  classes.root
                )}
                id="empid"
                name="empid"
                label="Employee Id"
                variant="outlined"
                margin="dense"
              />
            </div>
          </div>
          <div className="flex gap-5 flex-wrap sm:flex-nowrap mt-5">
            <div className="w-full lg:w-1/3 flex flex-col">
              <LocalizationProvider
                dateAdapter={AdapterDayjs}
                className="w-full"
              >
                <DemoContainer components={["DatePicker"]}>
                  <DatePicker
                    label="Date of Birth"
                    className={classNames(
                      "col-span-12 sm:col-span-6 xl:col-span-2",
                      classes.root
                    )}
                  />
                </DemoContainer>
              </LocalizationProvider>
            </div>

            <div className="w-full lg:w-1/3 flex flex-col">
              <FormControl
                variant="outlined"
                margin="dense"
                className={classNames(
                  "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                  classes.root
                )}
              >
                <InputLabel id="gender-label" className="w-52">
                  Gender
                </InputLabel>
                <Select
                  labelId="gender-label"
                  id="gender"
                  name="gender"
                  label="Gender"
                  IconComponent={(props) => (
                    // <div className="bg-red-500 z-50">
                    <ArrowDropDownRoundedIcon
                      {...props}
                      sx={{
                        fontSize: 40,
                        // marginLeft: "0.375rem",
                        // backgroundColor: "#bfdbfe",
                        borderRadius: 1,
                      }}
                      // className="bg-sky-200 mr-1.5 rounded-md cursor-pointer"
                    />
                    // </div>
                  )}
                >
                  <GlobalStyles />
                  <MenuItem value="male">Male</MenuItem>
                  <MenuItem value="female">Female</MenuItem>
                </Select>
              </FormControl>
            </div>
            <div className="w-full lg:w-1/3 flex flex-col">
              <FormControl
                variant="outlined"
                margin="dense"
                className={classNames(
                  "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                  classes.root
                )}
              >
                <InputLabel id="maritialstatus-label" className="w-52">
                  Maritial Status
                </InputLabel>
                <Select
                  labelId="maritialstatus-label"
                  id="maritialstatus"
                  name="maritialstatus"
                  label="Maritial Status"
                  IconComponent={(props) => (
                    <ArrowDropDownRoundedIcon
                      {...props}
                      sx={{ fontSize: 40 }}
                    />
                  )}
                >
                  <GlobalStyles />
                  <MenuItem value="single">Single</MenuItem>
                  <MenuItem value="married">Married</MenuItem>
                  <MenuItem value="divorced">Divorced</MenuItem>
                  <MenuItem value="widowed">Widowed</MenuItem>
                </Select>
              </FormControl>
            </div>
          </div>
        </div>
      </motion.div>
      <div className="w-full flex justify-end md:absolute md:z-0 md:bottom-36 md:right-10">
        <button
          type="button"
          onClick={onNext}
          className="bg-[#5336FD] text-white px-5 py-2 mt-8 rounded-md hover:scale-[1.020]"
        >
          Next
        </button>
      </div>
    </div>
  );
}

