export function WorkExperience({ onPrev }) {
  const [isSuccessPopupOpen, setIsSuccessPopupOpen] = useState(false);
  const [workExperiences, setWorkExperiences] = useState([
    { name: "Experience 1" },
  ]);
  const Navigate = useNavigate();
  let autoCloseTimer;
  const classes = useStyles();

  const handleAddMore = () => {
    const newExperience = { name: `Experience ${workExperiences.length + 1}` };
    setWorkExperiences([...workExperiences, newExperience]);
  };

  const handleRemove = (index) => {
    const updatedExperiences = [...workExperiences];
    updatedExperiences.splice(index, 1);
    setWorkExperiences(updatedExperiences);
  };

  useEffect(() => {
    let autoCloseTimer;
    if (isSuccessPopupOpen) {
      autoCloseTimer = setTimeout(() => {
        setIsSuccessPopupOpen(false);
        Navigate("/pim/employeelist");
      }, 5000);
    }
    return () => clearTimeout(autoCloseTimer);
  }, [isSuccessPopupOpen, Navigate]);

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsSuccessPopupOpen(true);
  };

  const handleGoToList = () => {
    setIsSuccessPopupOpen(false);
    Navigate("/pim/employeelist");
  };

  const [elecome, setElecome] = React.useState(true);
  return (
    <div className="">
      <div className="">
        <div className="flex items-center justify-between">
          <h4 className="font-bold">Work Experiences</h4>
          <button
            type="button"
            onClick={handleAddMore}
            className="bg-[#5336FD] text-white px-2 py-1 mt-2 rounded-md hover:scale-[1.020] flex items-center gap-1"
          >
            <IoAddCircle className="text-xl" />
            Add More
          </button>
        </div>
        <div className="mt-2">
          {workExperiences.map((workExp, index) => (
            <motion.div
              key={index}
              className="mb-5"
              animate={{ y: 0 }}
              initial={{ y: -20 }}
              transition={{ type: "spring", bounce: 0.7 }}
            >
              {index !== 0 && <hr className="mb-5" />}
              <div className="flex items-center">
                <h1>Experience {index + 1}</h1>
                <button
                  type="button"
                  onClick={() => {
                    handleRemove(index);
                    setElecome(!elecome);
                  }}
                  className="bg-sky-50 px-2 py-1 rounded-md text-xs ml-3 hover:bg-[#5336FD] hover:text-white flex items-center gap-1"
                >
                  {/* <IoIosRemoveCircle className="text-base" /> */}
                  Remove
                </button>
              </div>
              <div className="flex gap-5 flex-wrap sm:flex-nowrap mt-3">
                <div className="w-full lg:w-1/3 flex flex-col">
                  <TextField
                    className={classNames(
                      "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                      classes.root
                    )}
                    id="jobtitle"
                    name="jobtitle"
                    label="Job Title"
                    variant="outlined"
                    margin="dense"
                  />
                </div>
                <div className="w-full lg:w-1/3 flex flex-col">
                  <TextField
                    className={classNames(
                      "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                      classes.root
                    )}
                    id="companyname"
                    name="companyname"
                    label="Company Name"
                    variant="outlined"
                    margin="dense"
                  />
                </div>
                <div className="w-full lg:w-1/3 flex flex-col">
                  <TextField
                    className={classNames(
                      "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                      classes.root
                    )}
                    id="companyurl"
                    name="companyurl"
                    label="Company Website"
                    type="text"
                    variant="outlined"
                    margin="dense"
                  />
                </div>
              </div>
              <div className="flex gap-5 flex-wrap sm:flex-nowrap mt-5">
                <div className="w-full lg:w-1/3 flex flex-col">
                  <TextField
                    className={classNames(
                      "col-span-12 sm:col-span-6 xl:col-span-2 text-xs euclid",
                      classes.root
                    )}
                    id="location"
                    name="location"
                    label="Company Location"
                    type="text"
                    variant="outlined"
                    margin="dense"
                  />
                </div>

                <div className="w-full lg:w-1/3 flex flex-col">
                  <FormControl
                    variant="outlined"
                    margin="dense"
                    className={classNames(
                      "col-span-12 sm:col-span-6 xl:col-span-2",
                      classes.root
                    )}
                  >
                    <InputLabel id="emptype-label" className="w-52">
                      Emplyement Type
                    </InputLabel>
                    <Select
                      labelId="emptype-label"
                      id="emplyementtype"
                      name="emplyementtype"
                      label="Emplyement Type"
                      IconComponent={(props) => (
                        <ArrowDropDownRoundedIcon
                          {...props}
                          sx={{ fontSize: 40 }}
                        />
                      )}
                    >
                      <GlobalStyles />
                      <MenuItem value="full-time">Full-time</MenuItem>
                      <MenuItem value="part-time">Part-time</MenuItem>
                      <MenuItem value="contract">Contract</MenuItem>
                      <MenuItem value="temporary">Temporary</MenuItem>
                      <MenuItem value="freelance">
                        Freelance or Independent Contractor
                      </MenuItem>
                      <MenuItem value="internship">Internship</MenuItem>
                      <MenuItem value="remote">
                        Remote or Telecommuting
                      </MenuItem>
                    </Select>
                  </FormControl>
                </div>
                <div className="w-full lg:w-1/3 flex flex-col">
                  <TextField
                    className={classNames(
                      "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                      classes.root
                    )}
                    id="phone"
                    name="phone"
                    label="Phone Number"
                    type="number"
                    variant="outlined"
                    margin="dense"
                  />
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
      <div className="w-full flex justify-between  md:z-0 md:bottom-36 md:right-10  ">
        <button
          type="button"
          onClick={onPrev}
          className="bg-[#5336FD] text-white px-5 py-2 mt-8 rounded-md  hover:scale-[1.020]"
        >
          Previous
        </button>
        <button
          type="submit"
          value="Save Details"
          onClick={handleSubmit}
          className="bg-[#5336FD] text-white px-5 py-2 mt-8 rounded-md cursor-pointer hover:scale-[1.020]"
        >
          Save Details
        </button>
      </div>
      {isSuccessPopupOpen && (
        <PopupMessage
          className
          message="New Employee Added Successfully"
          onAddMore={handleAddMore}
          onGoToList={handleGoToList}
        />
      )}
    </div>
  );
}
