import React from "react";
import { NavLink, useLocation } from "react-router-dom";

const MenuTabs = () => {
  const location = useLocation();

  return (
    <div className="bg-white rounded-md p-2 sticky top-0 mb-2">
      <ul className="flex gap-2">
        <li
          className={`bg-[#EBE9F6] text-sm px-3 py-1.5 rounded-md ${
            location.pathname === "/pim/employeelist" ? "bg-[#5336FD]  font-bold " : ""
          }`}
        >
          <NavLink to="/pim/employeelist" activeClassName="bg-gray-200">
            Employee List
          </NavLink>
        </li>
        <li
          className={`bg-[#EBE9F6] text-sm px-3 py-1.5 rounded-md ${
            location.pathname === "/pim/addemployee" ? "bg-[#5336FD]  font-bold" : ""
          }`}
        >
          <NavLink to="/pim/addemployee" activeClassName="bg-gray-200">
            Add Employee
          </NavLink>
        </li>
      </ul>
    </div>
  );
};

export default MenuTabs;
