import React from "react";
import { DASHBOARD_SIDEBAR_LINKS } from "../../lib/consts/navigation";
import logo from "../../assets/images/invezza-logo.png";
import { Link, useLocation } from "react-router-dom";
import classNames from "classnames";

const LinkClasses =
  "flex hover:bg-[#EBE9F6] duration-500 p-3 mt-2 rounded-md euclid";

export default function Sidebar() {
  return (
    <div className="flex flex-col w-60 p-3 bg-white">
      <div className="pr-10">
        <img src={logo} alt="logo" />
      </div>
      <div className="flex-1 mt-10">
        {DASHBOARD_SIDEBAR_LINKS.map((item) => (
          <SidebarLink key={item.key} item={item} />
        ))}
      </div>

      <div>bottompart</div>
    </div>
  );
}

function SidebarLink({ item }) {
    const location = useLocation();
  
    // Check if the current location matches the link's path
    const isActive = location.pathname === item.path;
  
    // Define classes based on whether the link is active
    const linkClasses = classNames('flex', {
      'euclid-bold': isActive,
      'bg-[#3C5EFE] text-white hover:bg-[#3C5EFE]': isActive,
    });
  
    // Define classes for the ::after pseudo-element
    const afterClasses = classNames('after:w-1 after:bg-white after:rounded-full', {
      'hidden': !isActive, // Hide the pseudo-element if the link is not active
    });
  
    return (
      <div className={`hover:bg-[#EBE9F6] duration-500 p-3 mt-3 rounded-md ${isActive ? 'bg-[#EBE9F6]' : ''}`}>
        <Link to={item.path} className={linkClasses}>
          <span className="text-xl my-auto">{item.icon}</span>
          <span className="ml-3">{item.label}</span>
        </Link>
        <span className={afterClasses}></span> {/* Pseudo-element */}
      </div>
    );
  }
