import React, { useState } from "react";
import {
  Stepper,
  Step,
  StepLabel,
  Button,
  Typography,
} from "@material-ui/core";

function FormStepper() {
  const [activeStep, setActiveStep] = useState(0);

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  return (
    <div>
      <Stepper activeStep={activeStep} alternativeLabel>
        <Step>
          <StepLabel>Personal Information</StepLabel>
        </Step>
        <Step>
          <StepLabel>Employment Information</StepLabel>
        </Step>
        <Step>
          <StepLabel>Contact Information</StepLabel>
        </Step>
      </Stepper>
      <div>
        {activeStep === 3 ? (
          <div>
            <Typography>All steps completed</Typography>
          </div>
        ) : (
          <div>
            {activeStep === 0 && <PersonalInformation />}
            {activeStep === 1 && <EmploymentInformation />}
            {activeStep === 2 && <ContactInformation />}
            <div>
              <Button disabled={activeStep === 0} onClick={handleBack}>
                Back
              </Button>
              <Button variant="contained" color="primary" onClick={handleNext}>
                {activeStep === 2 ? "Finish" : "Next"}
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

function PersonalInformation() {
  return (
    <div>
      {/* Your Personal Information Component */}
      <h2>Personal Information</h2>
      {/* Your Personal Information Fields */}
    </div>
  );
}

function EmploymentInformation() {
  return (
    <div>
      {/* Your Employment Information Component */}
      <h2>Employment Information</h2>
      {/* Your Employment Information Fields */}
    </div>
  );
}

function ContactInformation() {
  return (
    <div>
      {/* Your Contact Information Component */}
      <h2>Contact Information</h2>
      {/* Your Contact Information Fields */}
    </div>
  );
}

export default FormStepper;
