import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  PersonalInformation,
  EmploymentInformation,
  ContactInformation,
  EmergencyContacts,
  WorkExperience,
} from "./Formsteps";

export default function Newempform() {
  const [isSuccessPopupOpen, setIsSuccessPopupOpen] = useState(false);
  const Navigate = useNavigate();

  const [formData, setFormData] = useState({
    personalInfo: {},
    employmentInfo: {},
    contactInfo: {},
    emergencyContacts: {},
    workExperience: {},
  });

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsSuccessPopupOpen(true);
    // Here you can submit formData to your backend
    console.log("Form submitted:", formData);
  };

  useEffect(() => {
    let autoCloseTimer;
    if (isSuccessPopupOpen) {
      autoCloseTimer = setTimeout(() => {
        setIsSuccessPopupOpen(false);
        Navigate("/pim/employeelist");
      }, 10000);
    }
    return () => clearTimeout(autoCloseTimer);
  }, [isSuccessPopupOpen, Navigate]);

  const [step, setStep] = useState(1);
  const steps = [
    "Personal Information",
    "Employment Information",
    "Contact Information",
    "Emergency Contacts",
    "Work Experience",
  ];

  const nextStep = () => {
    setStep((prevStep) => prevStep + 1);
  };

  const prevStep = () => {
    setStep((prevStep) => prevStep - 1);
  };

  const handleFormChange = (stepData) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      ...stepData,
    }));
  };

  return (
    <div className="bg-white p-4 rounded-md h-screen overflow-scroll scrollbar-hide flex flex-col md:grid grid-cols-12 gap-4">
      <div className="col-span-2">
        <div className="bg-sky-50 rounded-md sticky top-0">
          <ul className="text-sm p-2 flex flex-col gap-2 sticky top-0">
            {steps.map((stepName, index) => (
              <li
                key={index + 1}
                className={`hover:bg-blue-100 py-2.5 px-1.5 rounded-md cursor-pointer ${
                  step === index + 1 ? "bg-blue-100 font-bold" : ""
                }`}
                onClick={() => setStep(index + 1)}
              >
                {stepName}
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div className="col-span-10">
        <div>
          <form onSubmit={handleSubmit}>
            {step === 1 && (
              <PersonalInformation
                onNext={nextStep}
                formData={formData.personalInfo}
                onChange={(data) => handleFormChange({ personalInfo: data })}
              />
            )}
            {step === 2 && (
              <EmploymentInformation
                onNext={nextStep}
                onPrev={prevStep}
                formData={formData.employmentInfo}
                onChange={(data) => handleFormChange({ employmentInfo: data })}
              />
            )}
            {step === 3 && (
              <ContactInformation
                onNext={nextStep}
                onPrev={prevStep}
                formData={formData.contactInfo}
                onChange={(data) => handleFormChange({ contactInfo: data })}
              />
            )}
            {step === 4 && (
              <EmergencyContacts
                onNext={nextStep}
                onPrev={prevStep}
                formData={formData.emergencyContacts}
                onChange={(data) =>
                  handleFormChange({ emergencyContacts: data })
                }
              />
            )}
            {step === 5 && (
              <WorkExperience
                onPrev={prevStep}
                formData={formData.workExperience}
                onChange={(data) => handleFormChange({ workExperience: data })}
              />
            )}
          </form>
        </div>
      </div>
    </div>
  );
}
