import React from "react";
import { MantineProvider, Avatar } from "@mantine/core";
import { BiReset } from "react-icons/bi";
import { FaUpload } from "react-icons/fa6";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import PopupMessage from "./PopupMessage";
import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { DemoContainer } from "@mui/x-date-pickers/internals/demo";
import ArrowDropDownRoundedIcon from "@mui/icons-material/ArrowDropDownRounded";
import dayjs from "dayjs";

export default function Newempform() {
  const [selectedImage, setSelectedImage] = useState("");
  const [isSuccessPopupOpen, setIsSuccessPopupOpen] = useState(false);
  const Navigate = useNavigate();
  let autoCloseTimer;

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsSuccessPopupOpen(true); // Open the popup
    // Navigate("/pim/employeelist");
    // setTimeout(() => {
    //   setIsSuccessPopupOpen(false);
    // }, 2500);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleResetImage = () => {
    setSelectedImage("");
  };

  const handleAddMore = () => {
    setIsSuccessPopupOpen(false); // Close the popup
    clearTimeout(autoCloseTimer); // Cancel the auto close timer
  };

  const handleGoToList = () => {
    setIsSuccessPopupOpen(false);
    Navigate("/pim/employeelist");
  };

  useEffect(() => {
    let autoCloseTimer;
    if (isSuccessPopupOpen) {
      autoCloseTimer = setTimeout(() => {
        setIsSuccessPopupOpen(false); // Close the popup after 5 seconds
        Navigate("/pim/employeelist"); // Redirect to the employee list
      }, 10000);
    }
    return () => clearTimeout(autoCloseTimer);
  }, [isSuccessPopupOpen, Navigate]);

  return (
    <div className="bg-white p-4 rounded-md h-screen overflow-scroll scrollbar-hide">
      <div>
        <form onSubmit={handleSubmit} action="" method="post">
          <h4 className="font-bold">Add Profile Picture</h4>

          {/* user profile */}
          <div className="flex items-center md:items-center mt-4 gap-4">
            <div>
              <MantineProvider>
                <Avatar
                  className="w-28 md:w-20 bg-[#EBE9F6] p-2 rounded-md"
                  src={selectedImage || ""}
                  alt="Profile"
                />
              </MantineProvider>
            </div>
            <div className="flex items-center">
              <div className="flex flex-col md:flex-row">
                <label
                  htmlFor="upload-avatar"
                  className="bg-[#5336FD] px-3 py-2 flex text-white rounded-md cursor-pointer items-center hover:scale-[1.020] duration-150"
                >
                  <input
                    type="file"
                    id="upload-avatar"
                    accept="image/*"
                    onChange={handleImageUpload}
                    style={{ display: "none" }}
                  />
                  <FaUpload size={18} />
                  <h1 className="ml-2 text-xs font-bold">Upload Avatar</h1>
                </label>
                <div
                  className="bg-[#EBE9F6] px-3 py-2 flex rounded-md md:ml-4 mt-4 md:mt-0 cursor-pointer items-center hover:scale-[1.020] duration-150"
                  onClick={handleResetImage}
                >
                  <BiReset size={20} />
                  <h1 className="ml-2 text-xs font-bold">Reset Image</h1>
                </div>
              </div>
            </div>
          </div>

          {/* Personal Information */}
          <div className="mt-8">
            <h4 className="font-bold">Personal Information</h4>
            <div className="mt-2">
              <div className="flex gap-5 flex-wrap sm:flex-nowrap">
                <div className="w-full lg:w-1/3 flex flex-col">
                  {/* <label htmlFor="firstName">First Name</label>
                  <input
                    id="firstName"
                    name="firstName"
                    type="text"
                    className="bg-[#EBE9F6] rounded-lg md:mt-2 py-2.5 px-3 text-sm"
                  /> */}
                  <TextField
                    className="col-span-12 sm:col-span-6 xl:col-span-2 text-xs"
                    id="fname"
                    name="fname"
                    label="First Name"
                    variant="outlined"
                    margin="dense"
                    sx={{
                      "& .MuiInputLabel-root": {
                        fontSize: 14,
                      },
                      "& .MuiInputBase-root": {
                        backgroundColor: "#EBE9F6",
                        border: "0 none",
                        borderRadius: 2,
                        height: 50,
                      },
                      "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                      "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                      "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                    }}
                  />
                </div>
                <div className="w-full lg:w-1/3 flex flex-col">
                  {/* <label htmlFor="lastName">Last Name</label>
                  <input
                    id="lastName"
                    name="lastName"
                    type="text"
                    className="bg-[#EBE9F6] rounded-lg md:mt-2 py-2.5 px-3 text-sm"
                  /> */}
                  <TextField
                    className="col-span-12 sm:col-span-6 xl:col-span-2 text-xs"
                    id="lname"
                    name="lname"
                    label="Last Name"
                    variant="outlined"
                    margin="dense"
                    sx={{
                      "& .MuiInputLabel-root": {
                        fontSize: 14,
                      },
                      "& .MuiInputBase-root": {
                        backgroundColor: "#EBE9F6",
                        border: "0 none",
                        borderRadius: 2,
                        height: 50,
                      },
                      "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                      "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                      "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                    }}
                  />
                </div>
                <div className="w-full lg:w-1/3 flex flex-col">
                  {/* <label htmlFor="employeeId">Employee Id</label>
                  <input
                    id="employeeId"
                    name="employeeId"
                    type="text"
                    className="bg-[#EBE9F6] rounded-lg md:mt-2 py-2.5 px-3 text-sm"
                  /> */}
                  <TextField
                    className="col-span-12 sm:col-span-6 xl:col-span-2 text-xs"
                    id="empid"
                    name="empid"
                    label="Employee Id"
                    variant="outlined"
                    margin="dense"
                    sx={{
                      "& .MuiInputLabel-root": {
                        fontSize: 14,
                      },
                      "& .MuiInputBase-root": {
                        backgroundColor: "#EBE9F6",
                        border: "0 none",
                        borderRadius: 2,
                        height: 50,
                      },
                      "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                      "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                      "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                    }}
                  />
                </div>
              </div>
              <div className="flex gap-5 flex-wrap sm:flex-nowrap mt-5">
                <div className="w-full lg:w-1/3 flex flex-col">
                  {/* <label htmlFor="dateOfBirth">Date of Birth</label>
                  <input
                    id="dateOfBirth"
                    name="dateOfBirth"
                    type="date"
                    className="bg-[#EBE9F6] rounded-lg md:mt-2 py-2.5 px-3 text-sm"
                  /> */}
                  <LocalizationProvider
                    dateAdapter={AdapterDayjs}
                    className="w-full"
                  >
                    <DemoContainer components={["DatePicker"]}>
                      <DatePicker
                        defaultValue={dayjs("2022-04-17")}
                        views={["year", "month", "day"]}
                        sx={{
                          "& .MuiInputLabel-root": {
                            fontSize: 14,
                          },
                          "& .MuiInputBase-root": {
                            backgroundColor: "#EBE9F6",
                            border: "0 none",
                            borderRadius: 2,
                            height: 50,
                            width: "100%",
                            overflow: "hidden",
                          },
                          "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                            {
                              borderColor: "transparent",
                            },
                          "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                            {
                              borderColor: "transparent",
                            },
                          "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                            {
                              borderColor: "transparent",
                            },
                          "& ::placeholder": {
                            fontSize: 12,
                          },
                          display: "block", // Ensure DatePicker occupies full width
                          width: "100%", // Ensure DatePicker occupies full width
                        }}
                      />
                    </DemoContainer>
                  </LocalizationProvider>
                </div>

                <div className="w-full lg:w-1/3 flex flex-col">
                  {/* <label htmlFor="gender">Gender</label>
                  <select
                    id="gender"
                    name="gender"
                    className="bg-[#EBE9F6] rounded-lg md:mt-2 py-2.5 px-3 text-sm"
                  >
                    <option value=""></option>
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                  </select> */}
                  <FormControl
                    variant="outlined"
                    margin="dense"
                    className="col-span-12 sm:col-span-6 xl:col-span-2"
                    sx={{
                      "& .MuiInputLabel-root": {
                        fontSize: 14,
                      },
                      "& .MuiInputBase-root": {
                        backgroundColor: "#EBE9F6",
                        border: "0 none",
                        borderRadius: 2,
                        height: 50,
                      },
                      "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                      "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                      "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                    }}
                  >
                    <InputLabel id="gender-label" className="w-52">
                      Gender
                    </InputLabel>
                    <Select
                      labelId="gender-label"
                      id="gender"
                      name="gender"
                      label="Gender"
                      IconComponent={(props) => (
                        <ArrowDropDownRoundedIcon
                          {...props}
                          sx={{ fontSize: 40 }}
                        />
                      )}
                    >
                      <MenuItem value="male">Male</MenuItem>
