import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  PersonalInformation,
  EmploymentInformation,
  ContactInformation,
  EmergencyContacts,
  WorkExperience,
} from "./Formsteps";
import { motion } from "framer-motion";

export default function Newempform() {
  const [isSuccessPopupOpen, setIsSuccessPopupOpen] = useState(false);
  const [formData, setFormData] = useState({
    personalInfo: {},
    employmentInfo: {},
    contactInfo: {},
    emergencyContacts: {},
    workExperience: {},
  });
  const Navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    // Here you can submit formData to your backend
    console.log("Form submitted:", formData);
    setIsSuccessPopupOpen(true);
  };

  const handleFormChange = (stepData) => {
    setFormData((prevData) => {
      const updatedData = { ...prevData, ...stepData };
      console.log("Form data updated:", updatedData);
      return updatedData;
    });
  };

  useEffect(() => {
    let autoCloseTimer;
    if (isSuccessPopupOpen) {
      autoCloseTimer = setTimeout(() => {
        setIsSuccessPopupOpen(false);
        Navigate("/pim/employeelist");
      }, 10000);
    }
    return () => clearTimeout(autoCloseTimer);
  }, [isSuccessPopupOpen, Navigate]);

  const [step, setStep] = useState(1);
  const steps = [
    "Personal Information",
    "Employment Information",
    "Contact Information",
    "Emergency Contacts",
    "Work Experience",
  ];

  const nextStep = () => {
    setStep((prevStep) => prevStep + 1);
  };

  const prevStep = () => {
    setStep((prevStep) => prevStep - 1);
  };

  return (
    <motion.div
      className="bg-white p-4 rounded-md h-screen overflow-scroll scrollbar-hide flex flex-col md:grid grid-cols-12 gap-4"
      initial={{ opacity: 0, y: 15 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="  col-span-2 ">
        <div className="bg-sky-50 rounded-md sticky top-0">
          <ul className="text-sm p-2 flex flex-col gap-2 sticky top-0">
            {steps.map((stepName, index) => (
              <li
                key={index + 1}
                className={`hover:bg-blue-100 py-2.5 px-1.5 rounded-md cursor-pointer ${
                  step === index + 1 ? "bg-blue-100 font-bold" : ""
                }`}
                onClick={() => setStep(index + 1)}
              >
                {stepName}
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div className="col-span-10">
        <div>
          <form onSubmit={handleSubmit}>
            {step === 1 && (
              <PersonalInformation
                onNext={nextStep}
                onChange={handleFormChange}
              />
            )}
            {step === 2 && (
              <EmploymentInformation
                onNext={nextStep}
                onPrev={prevStep}
                onChange={handleFormChange}
              />
            )}
            {step === 3 && (
              <ContactInformation
                onNext={nextStep}
                onPrev={prevStep}
                onChange={handleFormChange}
              />
            )}
            {step === 4 && (
              <EmergencyContacts
                onNext={nextStep}
                onPrev={prevStep}
                onChange={handleFormChange}
              />
            )}
            {step === 5 && (
              <WorkExperience onPrev={prevStep} onChange={handleFormChange} />
            )}
            {step === 5 && (
              <button
                type="submit"
                className="bg-blue-500 text-white px-4 py-2 rounded-md mt-4"
              >
                Submit
              </button>
            )}
          </form>
        </div>
      </div>
    </motion.div>
  );
}
