import React, { useState } from "react";
import { useParams } from "react-router-dom";
import MenuTabs from "./Menutabs";
import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import classNames from "classnames";
import { makeStyles } from "@mui/styles";
import { createGlobalStyle } from "styled-components";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { DemoContainer } from "@mui/x-date-pickers/internals/demo";
import dayjs from "dayjs";
import ArrowDropDownRoundedIcon from "@mui/icons-material/ArrowDropDownRounded";

const GlobalStyles = createGlobalStyle`
.MuiPaper-root{
  height:215px;
  border-radius:10px;
} 
  .MuiMenuItem-root {
    font-family: Euclid;
    font-size: 14px;
    font-weight: bold;
    margin: auto 8px;
    border-radius: 7px;
  }
  .MuiMenuItem-root:hover {
    background-color:#e0f2fe;
    padding-left: 14px;
  }
  .MuiMenuItem-root:hover {
    transition-duration: 0.2s;
  }

  ::-webkit-scrollbar {
    display: none;
    -ms-overflow-style: none;
    scrollbar-width: none;
}
`;

const useStyles = makeStyles({
  root: {
    "& .MuiInputLabel-root": {
      fontFamily: "euclid",
      fontSize: 14,
      paddingTop: -2.5,
      fontWeight: "bold",
    },
    "& .MuiInputLabel-root.Mui-focused": {
      color: "black",
      fontWeight: "bold",
      fontSize: 15,
    },
    "& .MuiInputBase-root": {
      backgroundColor: "#f0f9ff",
      border: "0 none",
      borderRadius: 7,
      height: 50,
      width: "100%",
      overflow: "hidden",
    },
    "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent",
    },
    "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent",
    },
    "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline": {
      borderColor: "gray",
    },
    "& .Muilplaceholder": {
      fontFamily: "euclid",
      fontSize: 10,
    },
    "& .MuiOutlinedInput-input": {
      fontFamily: "euclid-medium",
      fontSize: 14,
    },
    "& ::placeholder": {
      fontSize: 12,
    },
    "& JoyCheckbox-input": {
      backgroundColor: "red",
    },
    display: "block",
    width: "100%",
    fontFamily: "euclid-medium",
  },
});

export default function EditEmployee({ updateEmployee }) {
  const classes = useStyles();

  const {
    empid,
    ename: initialName,
    designation: initialDesignation,
    jdate: initialJoiningDate,
    status: initialStatus,
  } = useParams();

  // State variables to store the edited values
  const [editedName, setEditedName] = useState(initialName);
  const [editedDesignation, setEditedDesignation] =
    useState(initialDesignation);
  const [editedJoiningDate, setEditedJoiningDate] =
    useState(initialJoiningDate);
  const [editedStatus, setEditedStatus] = useState(initialStatus);

  // Function to handle saving the edited information
  const handleSave = () => {
    const [rows, setRows] = useState([
      // Initial data
      // Include all initial data here...
    ]);
    // Call the updateEmployee function from the parent component
    const updateEmployee = (
      empId,
      updatedName,
      updatedDesignation,
      updatedJoiningDate,
      updatedStatus
    ) => {
      setRows(
        rows.map((row) => {
          if (row.empid === empId) {
            // Update the fields for the specific employee
            return {
              ...row,
              ename: updatedName,
              designation: updatedDesignation,
              jdate: updatedJoiningDate,
              status: updatedStatus,
            };
          }
          return row;
        })
      );
    };
    // You can also add logic here to navigate back or perform other actions after saving
  };

  return (
    <div>
      <MenuTabs />
      <div className="flex flex-col gap-4 bg-white p-4 rounded-md">
        <div className="flex flex-row  w-1/3">
          <TextField
            className={classNames(
              "flex flex-row",
              "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
              classes.root
            )}
            label="Employee ID"
            value={empid}
            variant="outlined"
            margin="dense"
            InputLabelProps={{ shrink: true }}
            InputProps={{ readOnly: true }}
          />
        </div>
        <div className="flex flex-row w-1/3">
          <TextField
            className={classNames(
              "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
              classes.root
            )}
            id="name"
            name="name"
            label="Name"
            variant="outlined"
            margin="dense"
            value={editedName}
            onChange={(e) => setEditedName(e.target.value)}
          />
        </div>
        <div className="flex flex-row w-1/3">
          <TextField
            className={classNames(
              "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
              classes.root
            )}
            id="designation"
            name="designation"
            label="Designation"
            variant="outlined"
            margin="dense"
            value={editedDesignation}
            onChange={(e) => setEditedDesignation(e.target.value)}
          />
        </div>
        <div className="w-full lg:w-1/3 flex flex-col">
          <LocalizationProvider dateAdapter={AdapterDayjs} className="w-full">
            <DemoContainer components={["DatePicker"]}>
              <DatePicker
                label="Joining Date"
                value={dayjs(editedJoiningDate)}
                className={classNames(
                  "col-span-12 sm:col-span-6 xl:col-span-2",
                  classes.root
                )}
                onChange={(date) => setEditedJoiningDate(date)}
                renderInput={(params) => <TextField {...params} />}
              />
            </DemoContainer>
          </LocalizationProvider>
        </div>
        <div className="flex flex-row w-1/3">
          {/* <label>
            Status:
            <select
              value={editedStatus}
              onChange={(e) => setEditedStatus(e.target.value)}
            >
              <GlobalStyles />
              <option>Active</option>
              <option>Inactive</option>
              <option>Removed</option>
            </select>
          </label> */}
          <FormControl
            variant="outlined"
            margin="dense"
            className={classNames(
              "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
              classes.root
            )}
          >
            <InputLabel id="status-label">Status</InputLabel>
            <Select
              labelId="status-label"
              id="status"
              value={editedStatus}
              onChange={(e) => setEditedStatus(e.target.value)}
              label="Status"
              IconComponent={(props) => (
                <ArrowDropDownRoundedIcon {...props} sx={{ fontSize: 40 }} />
              )}
            >
              <GlobalStyles />
              <MenuItem value="Active">Active</MenuItem>
              <MenuItem value="Inactive">Inactive</MenuItem>
              <MenuItem value="Removed">Removed</MenuItem>
            </Select>
          </FormControl>
        </div>
        <button onClick={handleSave}>Save</button>
      </div>
    </div>
  );
}
