import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { IoLogIn, IoLogOut } from "react-icons/io5";

const ONE_HOUR_IN_SECONDS = 3600;
const NINE_HOURS_IN_SECONDS = 1 * ONE_HOUR_IN_SECONDS;

export default function Greeting() {
  const [isPunchedIn, setIsPunchedIn] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const [remainingTime, setRemainingTime] = useState(NINE_HOURS_IN_SECONDS);

  useEffect(() => {
    const storedStartTime = localStorage.getItem("startTime");
    if (storedStartTime) {
      setStartTime(parseInt(storedStartTime, 10));
      const elapsedTime = Math.floor(
        (Date.now() - parseInt(storedStartTime, 10)) / 1000
      );
      const newRemainingTime = Math.max(0, NINE_HOURS_IN_SECONDS - elapsedTime);
      setRemainingTime(newRemainingTime);
      setIsPunchedIn(true);
    }
  }, []);

  useEffect(() => {
    let timer;
    if (isPunchedIn) {
      localStorage.setItem("startTime", Date.now());
      timer = setInterval(() => {
        setRemainingTime((prevTime) => {
          if (prevTime <= 0) {
            clearInterval(timer);
            setIsPunchedIn(false);
            localStorage.removeItem("startTime");
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);
    } else {
      clearInterval(timer);
      localStorage.removeItem("startTime");
    }
    return () => clearInterval(timer);
  }, [isPunchedIn]);

  const handleClick = () => {
    setIsPunchedIn(!isPunchedIn);
  };

  const formatTime = (timeInSeconds) => {
    const hours = Math.floor(timeInSeconds / ONE_HOUR_IN_SECONDS);
    const minutes = Math.floor((timeInSeconds % ONE_HOUR_IN_SECONDS) / 60);
    const seconds = timeInSeconds % 60;
    return `${hours}:${minutes.toString().padStart(2, "0")}:${seconds
      .toString()
      .padStart(2, "0")}`;
  };

  const progressBarStyle = {
    width: `${
      ((NINE_HOURS_IN_SECONDS - remainingTime) / NINE_HOURS_IN_SECONDS) * 100
    }%`,
    height: "4px",
    backgroundColor: isPunchedIn ? "#f44336" : "#4caf50",
    transition: "width 1s",
  };

  return (
    <div>
      <div className="flex justify-between">
        <div className="">
          <div className="flex flex-col gap-1">
            <h2 className="text-2xl font-bold text-[#5336FD]">
              Good Morning Shubham
            </h2>
            <h2>You have 24 new task. It’s a lots of work today!</h2>
          </div>
          <Link to="/Task">
            <button className="bg-[#5336FD] px-4 py-2 text-white rounded-md">
              View Tasks
            </button>
          </Link>
        </div>

        <div className="flex flex-col w-1/2">
          <div className="flex">
            <button
              className={`px-2 py-1.5 rounded-md flex items-center gap-1 text-sm ${
                isPunchedIn ? "bg-red-500" : "bg-green-500"
              } text-white transition-all duration-700`}
              onClick={handleClick}
            >
              {isPunchedIn ? (
                <>
                  Punch Out <IoLogOut fontSize={20} />
                </>
              ) : (
                <>
                  Punch In <IoLogIn fontSize={20} />
                </>
              )}
            </button>
            <span>({formatTime(remainingTime)})</span>
          </div>
          <div className="bg-green-500 w-1/2 mt-28">
            <div style={progressBarStyle} className="h-10"></div>
          </div>
        </div>
      </div>
    </div>
  );
}
