import React, { useState } from "react";
import { Link } from "react-router-dom";
import Menutabs from "./Menutabs";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TablePagination from "@mui/material/TablePagination";
import TableRow from "@mui/material/TableRow";
import { MdOutlinePreview } from "react-icons/md";
import { FaEdit } from "react-icons/fa";
import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { FaFilterCircleXmark } from "react-icons/fa6";
import ArrowDropDownRoundedIcon from "@mui/icons-material/ArrowDropDownRounded";
import { makeStyles } from "@mui/styles";
import { createGlobalStyle } from "styled-components";
import classNames from "classnames";

const useStyles = makeStyles({
  root: {
    "& .MuiInputLabel-root": {
      fontFamily: "euclid",
      fontSize: 14,
      paddingTop: -2.5,
      fontWeight: "bold",
    },
    "& .MuiInputLabel-root.Mui-focused": {
      color: "black",
      fontWeight: "bold",
      fontSize: 15,
    },
    "& .MuiInputBase-root": {
      backgroundColor: "#f0f9ff",
      border: "0 none",
      borderRadius: 7,
      height: 50,
      width: "100%",
      overflow: "hidden",
    },
    "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent",
    },
    "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent",
    },
    "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline": {
      borderColor: "gray",
    },
    "& .Muilplaceholder": {
      fontFamily: "euclid",
      fontSize: 10,
    },
    "& .MuiOutlinedInput-input": {
      fontFamily: "euclid-medium",
      fontSize: 14,
    },
    "& ::placeholder": {
      fontSize: 12,
    },
    display: "block",
    width: "100%",
  },
});

const GlobalStyles = createGlobalStyle`
.MuiPaper-root{
  height:215px;
  border-radius:10px;
} 
.MuiMenuItem-root {
    font-family: Euclid;
    font-size: 14px;
    font-weight: bold;
    margin: auto 8px;
    border-radius: 7px;
  }
  .MuiMenuItem-root:hover {
    background-color:#e0f2fe;
    padding-left: 15px;
  }
  .MuiMenuItem-root:hover {
    transition-duration: 0.2s;
  }

  ::-webkit-scrollbar {
    display: none;
    -ms-overflow-style: none;
    scrollbar-width: none;
    
}
`;

const columns = [
  { id: "empid", label: "Employee ID", minWidth: 120 },
  { id: "ename", label: "Employee Name", minWidth: 120 },
  { id: "designation", label: "Designation", minWidth: 120 },
  { id: "jdate", label: "Joining Date", minWidth: 120 },
  { id: "status", label: "Status", minWidth: 120 },
  { id: "actions", label: "Actions", minWidth: 80 },
];

function createData(empid, ename, designation, jdate, status) {
  return { empid, ename, designation, jdate, status };
}

const [rows, setRows] = useState([
  createData("EMP001", "John Doe", "Software Engineer", "01/01/2022", "Active"),
  createData(
    "EMP002",
    "Jane Smith",
    "Project Manager",
    "05/15/2021",
    "Inactive"
  ),
  createData(
    "EMP003",
    "Alice Johnson",
    "Data Analyst",
    "03/20/2023",
    "Inactive"
  ),
  createData(
    "EMP004",
    "Michael Brown",
    "Software Developer",
    "11/10/2021",
    "Active"
  ),
  createData(
    "EMP005",
    "Emma Garcia",
    "Business Analyst",
    "07/08/2022",
    "Active"
  ),
  createData(
    "EMP006",
    "William Martinez",
    "Quality Assurance",
    "09/25/2023",
    "Active"
  ),
  createData(
    "EMP007",
    "Olivia Anderson",
    "UI/UX Designer",
    "04/05/2021",
    "Inactive"
  ),
  createData(
    "EMP008",
    "James Wilson",
    "System Administrator",
    "02/14/2022",
    "Active"
  ),
  createData(
    "EMP009",
    "Ella Taylor",
    "Network Engineer",
    "06/30/2023",
    "Active"
  ),
