import React, { useState } from "react";
import { DASHBOARD_SIDEBAR_LINKS } from "../../lib/consts/navigation";
import logo from "../../assets/images/invezza-logo.png";
import { Link, useLocation } from "react-router-dom";
import classNames from "classnames";
import { IoExit } from "react-icons/io5";
import { TbLayoutSidebarLeftCollapseFilled } from "react-icons/tb";

const LinkClasses =
  "flex hover:bg-[#EBE9F6] hover:duration-500 p-3 mt-1.5 rounded-md euclid";

export default function Sidebar() {
  const { pathname } = useLocation();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = (e) => {
    // Prevent toggling if the click event originates from the button with the TbLayoutSidebarLeftCollapseFilled icon
    if (e.target.classList.contains("icon-collapse")) {
      e.preventDefault();
    } else {
      setIsSidebarOpen(!isSidebarOpen);
    }
  };

  return (
    <div>
      <button
        className="md:hidden fixed top-0 left-0 p-3 text-white bg-gray-900"
        onClick={toggleSidebar}
      >
        Toggle Sidebar
      </button>
      <div
        className={`md:flex md:flex-col w-60 h-screen absolute md:relative p-4 bg-white ${
          isSidebarOpen ? "" : "hidden"
        }`}
      >
        <div className=" flex flex-row justify-between items-center">
          <img src={logo} className="pr-16" alt="logo" />
          <TbLayoutSidebarLeftCollapseFilled className="text-2xl absolute flex md:hidden right-4 mt-3 icon-collapse" />
        </div>
        <div className="flex-1 mt-10">
          {DASHBOARD_SIDEBAR_LINKS.map((item) => (
            <SidebarLink key={item.key} item={item} pathname={pathname} />
          ))}
        </div>

        <div className="flex flex-col gap-0.5 pt-2 border-t border-neutral-400">
          <div className={classNames("", LinkClasses)}>
            <span className="text-xl my-auto text-red-500">
              <IoExit />
            </span>
            <span className="ml-3 w-[90%] text-red-500">Logout</span>
          </div>
        </div>
      </div>
    </div>
  );
}

function SidebarLink({ item, pathname }) {
  return (
    <Link
      to={item.path}
      className={classNames(
        pathname === item.path
          ? "euclid-bold bg-[#EBE9F6]  after:w-1.5 after:bg-[#3C5EFE] after:rounded-full"
          : "",
        LinkClasses
      )}
    >
      <span className="text-xl my-auto">{item.icon}</span>
      <span className="ml-3 w-[90%]">{item.label}</span>
    </Link>
  );
}
