import * as React from "react";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TablePagination from "@mui/material/TablePagination";
import TableRow from "@mui/material/TableRow";

const columns = [
  { id: "fname", label: "First Name", minWidth: 120 },
  { id: "lname", label: "Last Name", minWidth: 120 },
  { id: "empid", label: "Employee ID", minWidth: 120 },
  { id: "designation", label: "Designation", minWidth: 170 },
  { id: "jdate", label: "Joining Date", minWidth: 150 },
  { id: "tl", label: "Team Lead", minWidth: 120 },
  { id: "manager", label: "Manager", minWidth: 120 },
];

function createData(fname, lname, empid, designation, jdate, tl, manager) {
  return { fname, lname, empid, designation, jdate, tl, manager };
}

const rows = [
  createData(
    "John",
    "Doe",
    "EMP001",
    "Software Engineer",
    "01/01/2022",
    "Sarah",
    "Mike"
  ),
  createData(
    "Jane",
    "Smith",
    "EMP002",
    "Project Manager",
    "05/15/2021",
    "Alice",
    "Tom"
  ),
];

export default function StickyHeadTable() {
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  return (
    <div>
      <div className="bg-white rounded-md p-2 sticky top-0 mb-2">
        <ul className="flex gap-2">
          <li className="bg-[#EBE9F6] text-sm px-3 py-1.5 rounded-md">
            Employee List
          </li>
          <li className="bg-[#EBE9F6] text-sm px-3 py-1.5 rounded-md">
            Add Employee
          </li>
        </ul>
      </div>

      <Paper
        sx={{ overflow: "hidden" }}
        className="md:w-[100%] w-[calc(100vw-0.8rem)]"
      >
        <TableContainer sx={{ maxHeight: 440 }}>
          <Table stickyHeader aria-label="sticky table">
            <TableHead className={`bg-red-600 text-white`}>
              <TableRow>
                {" "}
                {/* Apply Tailwind CSS classes using template literals */}
                {columns.map((column) => (
                  <TableCell
                    key={column.id}
                    align="left"
                    style={{ minWidth: column.minWidth }}
                  >
                    {column.label}
                  </TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {rows
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map((row) => {
                  return (
                    <TableRow
                      hover
                      role="checkbox"
                      tabIndex={-1}
                      key={row.empid}
                    >
                      {columns.map((column) => {
                        const value = row[column.id];
                        return (
                          <TableCell key={column.id} align="left">
                            {value}
                          </TableCell>
                        );
                      })}
                    </TableRow>
                  );
                })}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          rowsPerPageOptions={[10, 25, 100]}
          component="div"
          count={rows.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
        />
      </Paper>
    </div>
  );
}
