import React from "react";
import { useLocation } from "react-router-dom"; // Import `useLocation` hook

const ViewEmployee = () => {
  const location = useLocation();
  const employee = location.state?.employee; // Access the employee data from state

  if (!employee) {
    return <div>No employee data found.</div>; // Handle cases where no data is passed
  }

  return (
    <div className="employee-details">
      <p>Employee ID: {employee.empid}</p>
      <p>Name: {employee.ename}</p>
      {/* ... other employee details */}
    </div>
  );
};

export default ViewEmployee;
