const VisuallyHiddenInput = styled("input")({
  clip: "rect(0 0 0 0)",
  clipPath: "inset(50%)",
  height: 1,
  overflow: "hidden",
  position: "absolute",
  bottom: 0,
  left: 0,
  whiteSpace: "nowrap",
  width: 1,
});

const GlobalStyles = createGlobalStyle`
.MuiPaper-root{
  height:215px;
  border-radius:10px;
} 
  .MuiMenuItem-root {
    font-family: Euclid;
    font-size: 14px;
    font-weight: bold;
    margin: auto 8px;
    border-radius: 7px;
  }
  .MuiMenuItem-root:hover {
    background-color:#e0f2fe;
    padding-left: 14px;
  }
  .MuiMenuItem-root:hover {
    transition-duration: 0.2s;
  }

  ::-webkit-scrollbar {
    display: none;
    -ms-overflow-style: none;
    scrollbar-width: none;
}
`;

const useStyles = makeStyles({
  root: {
    "& .MuiInputLabel-root": {
      fontFamily: "euclid",
      fontSize: 14,
      paddingTop: -2.5,
      fontWeight: "bold",
    },
    "& .MuiInputLabel-root.Mui-focused": {
      color: "black",
      fontWeight: "bold",
      fontSize: 15,
    },
    "& .MuiInputBase-root": {
      backgroundColor: "#f0f9ff",
      border: "0 none",
      borderRadius: 7,
      height: 50,
      width: "100%",
      overflow: "hidden",
    },
    "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent",
    },
    "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline": {
      borderColor: "transparent",
    },
    "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline": {
      borderColor: "gray",
    },
    "& .Muilplaceholder": {
      fontFamily: "euclid",
      fontSize: 10,
    },
    "& .MuiOutlinedInput-input": {
      fontFamily: "euclid-medium",
      fontSize: 14,
    },
    "& ::placeholder": {
      fontSize: 12,
    },
    "& JoyCheckbox-input": {
      backgroundColor: "red",
    },
    display: "block",
    width: "100%",
    fontFamily: "euclid-medium",
  },
});

export function PersonalInformation({ onNext }) {
  const [selectedImage, setSelectedImage] = useState("");
  const classes = useStyles();

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setSelectedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleResetImage = () => {
    setSelectedImage("");
  };
  return (
    <div className="">
      <div>
        <h4 className="font-bold">Personal Information</h4>
        <motion.div
          className="mt-2"
          initial={{ opacity: 0, y: 15 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          <div className="flex items-center md:items-center mt-4 gap-4">
            <div>
              <div className="w-28 md:w-20 bg-sky-50 p-2 rounded-md flex justify-center">
                {selectedImage ? (
                  <img
                    src={selectedImage}
                    className="w-full h-full object-cover rounded-md"
                    alt="Profile"
                  />
                ) : (
                  <FaUserAlt className="text-6xl text-gray-300" />
                )}
              </div>
            </div>
            <div className="flex items-center">
              <div className="flex flex-col md:flex-row">
                <label
                  htmlFor="upload-avatar"
                  className="bg-[#5336FD] px-3 py-2 flex text-white rounded-md cursor-pointer items-center hover:scale-[1.020] duration-150"
                >
                  <input
                    type="file"
                    id="upload-avatar"
                    accept="image/*"
                    onChange={handleImageUpload}
                    style={{ display: "none" }}
                  />
                  <FaUpload size={18} />
                  <h1 className="ml-2 text-xs font-bold">Upload Avatar</h1>
                </label>
                <div
                  className="bg-sky-50 px-3 py-2 flex rounded-md md:ml-4 mt-4 md:mt-0 cursor-pointer items-center hover:scale-[1.020] duration-150"
                  onClick={handleResetImage}
                >
                  <BiReset size={20} />
                  <h1 className="ml-2 text-xs font-bold">Reset Image</h1>
                </div>
              </div>
            </div>
          </div>
          <div className="mt-5">
            <div className="flex gap-5 flex-wrap sm:flex-nowrap">
              <div className="w-full lg:w-1/3 flex flex-col">
                <TextField
                  className={classNames(
                    "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                    classes.root
                  )}
                  id="fname"
                  name="fname"
                  label="First Name"
                  variant="outlined"
                  margin="dense"
                />
              </div>
              <div className="w-full lg:w-1/3 flex flex-col">
                <TextField
                  className={classNames(
                    "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                    classes.root
                  )}
                  id="lname"
                  name="lname"
                  label="Last Name"
                  variant="outlined"
                  margin="dense"
                />
              </div>
              <div className="w-full lg:w-1/3 flex flex-col">
                <TextField
                  className={classNames(
                    "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                    classes.root
                  )}
                  id="empid"
                  name="empid"
                  label="Employee Id"
                  variant="outlined"
                  margin="dense"
                />
              </div>
            </div>
            <div className="flex gap-5 flex-wrap sm:flex-nowrap mt-5">
              <div className="w-full lg:w-1/3 flex flex-col">
                <LocalizationProvider
                  dateAdapter={AdapterDayjs}
                  className="w-full"
                >
                  <DemoContainer components={["DatePicker"]}>
                    <DatePicker
                      label="Date of Birth"
                      className={classNames(
                        "col-span-12 sm:col-span-6 xl:col-span-2",
                        classes.root
                      )}
                    />
                  </DemoContainer>
                </LocalizationProvider>
              </div>

              <div className="w-full lg:w-1/3 flex flex-col">
                <FormControl
                  variant="outlined"
                  margin="dense"
                  className={classNames(
                    "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                    classes.root
                  )}
                >
                  <InputLabel id="gender-label" className="w-52">
                    Gender
                  </InputLabel>
                  <Select
                    labelId="gender-label"
                    id="gender"
                    name="gender"
                    label="Gender"
                    IconComponent={(props) => (
                      // <div className="bg-red-500 z-50">
                      <ArrowDropDownRoundedIcon
                        {...props}
                        sx={{
                          fontSize: 40,
                          // marginLeft: "0.375rem",
                          // backgroundColor: "#bfdbfe",
                          borderRadius: 1,
                        }}
                        // className="bg-sky-200 mr-1.5 rounded-md cursor-pointer"
                      />
                      // </div>
                    )}
                  >
                    <GlobalStyles />
                    <MenuItem value="male">Male</MenuItem>
                    <MenuItem value="female">Female</MenuItem>
                  </Select>
                </FormControl>
              </div>
              <div className="w-full lg:w-1/3 flex flex-col">
                <FormControl
                  variant="outlined"
                  margin="dense"
                  className={classNames(
                    "col-span-12 sm:col-span-6 xl:col-span-2 text-xs",
                    classes.root
                  )}
                >
                  <InputLabel id="maritialstatus-label" className="w-52">
                    Maritial Status
                  </InputLabel>
                  <Select
                    labelId="maritialstatus-label"
                    id="maritialstatus"
                    name="maritialstatus"
                    label="Maritial Status"
                    IconComponent={(props) => (
                      <ArrowDropDownRoundedIcon
                        {...props}
                        sx={{ fontSize: 40 }}
                      />
                    )}
                  >
                    <GlobalStyles />
                    <MenuItem value="single">Single</MenuItem>
                    <MenuItem value="married">Married</MenuItem>
                    <MenuItem value="divorced">Divorced</MenuItem>
                    <MenuItem value="widowed">Widowed</MenuItem>
                  </Select>
                </FormControl>
              </div>
            </div>
          </div>
        </motion.div>
      </div>
      <div className="w-full flex justify-end md:absolute md:z-0 mt-8 md:bottom-36 md:right-10">
        <button
          type="button"
          onClick={onNext}
          className="bg-[#5336FD] text-white px-4 py-2 rounded-md hover:scale-[1.020]"
        >
          <div className="flex items-center gap-2">
            Next
            <TbPlayerTrackNextFilled />
          </div>
        </button>
      </div>
    </div>
  );
}

