import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function ViewEmployee() {
  const { empid } = useParams();
  const [employee, setEmployee] = useState(null);

  useEffect(() => {
    console.log("empid:", empid); // Check if empid is received
    fetchEmployee(empid);
  }, [empid]);

  const fetchEmployee = async (empid) => {
    try {
      // Replace this with your actual API call or data fetching logic
      const response = await fetch(`/api/employees/${empid}`);
      const data = await response.json();
      setEmployee(data); // Set the fetched employee data to state
    } catch (error) {
      console.error("Error fetching employee details:", error);
    }
  };

  if (!employee) {
    return <div>Loading...</div>; // Show loading indicator while data is being fetched
  }

  return (
    <div>
      <h2>Employee Details</h2>
      <p>Employee ID: {employee.empid}</p>
      <p>Name: {employee.ename}</p>
      <p>Designation: {employee.designation}</p>
      <p>Joining Date: {employee.jdate}</p>
      <p>Status: {employee.status}</p>
      {/* Add more details as needed */}
    </div>
  );
}

export default ViewEmployee;
