import React from "react";
import { DASHBOARD_SIDEBAR_LINKS } from "../../lib/consts/navigation";
import logo from "../../assets/images/invezza-logo.png";
import { Link, useLocation } from "react-router-dom";
import classNames from "classnames";

const LinkClasses =
  "flex hover:bg-[#EBE9F6] duration-500 p-3 mt-2 rounded-md euclid-bold active:bg-neutral-600";

export default function Sidebar() {
  return (
    <div className="flex flex-col w-60 p-3 bg-white">
      <div className="pr-10">
        <img src={logo} alt="logo" />
      </div>
      <div className="flex-1 mt-10">
        {DASHBOARD_SIDEBAR_LINKS.map((item) => (
          <SidebarLink key={item.key} item={item} />
        ))}
      </div>

      <div>bottompart</div>
    </div>
  );
}

function SidebarLink({ item }) {
  const { pathname } = useLocation();

  return (
    <Link to={item.path} className={classNames(pathname == item.path ? 'euclid-medium' : '', LinkClasses)}>
      <span className="text-xl my-auto ">{item.icon}</span>
      <span className="ml-3">{item.label}</span>
    </Link>
  );
}
