import React, { useState, useEffect } from "react";
import Menutabs from "./Menutabs";
import { useParams } from "react-router-dom";

export default function ViewEmployee() {
  const { empid } = useParams();
  const [employeeData, setEmployeeData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch employee data using empid
    fetch(`/Employeelist/${empid}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setEmployeeData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching employee data:", error);
        setError(error);
        setLoading(false);
      });
  }, [empid]);

  if (loading) {
    return <div>Loading...</div>;
  }

  // if (error) {
  //   return <div>Error: {error.message}</div>;
  // }

  return (
    <div>
      <Menutabs />
      <h1>View Employee {empid}</h1>
      {/* Display employee details */}
      {employeeData && (
        <div>
          <p>Employee ID: {employeeData.empid}</p>
          <p>Name: {employeeData.ename}</p>
          <p>Designation: {employeeData.designation}</p>
          <p>Joining Date: {employeeData.jdate}</p>
          <p>Status: {employeeData.status}</p>
          {/* Add other employee details as needed */}
        </div>
      )}
    </div>
  );
}
