import React, { useState, useEffect } from "react";
import { IoLogIn, IoLogOut } from "react-icons/io5";
import { Link } from "react-router-dom";

const ONE_HOUR_IN_SECONDS = 3600;
const NINE_HOURS_IN_SECONDS = 0.5 * ONE_HOUR_IN_SECONDS;

export default function Greeting() {
  const [isPunchedIn, setIsPunchedIn] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const [remainingTime, setRemainingTime] = useState(NINE_HOURS_IN_SECONDS);
  const [punchInTime, setPunchInTime] = useState(null);
  const [punchOutTime, setPunchOutTime] = useState(null);

  useEffect(() => {
    const storedStartTime = localStorage.getItem("startTime");
    const storedPunchInTime = localStorage.getItem("punchInTime");
    const storedPunchOutTime = localStorage.getItem("punchOutTime");
    if (storedStartTime) {
      const currentTime = Date.now();
      const elapsedTime = Math.floor(
        (currentTime - parseInt(storedStartTime, 10)) / 1000
      );
      if (elapsedTime < NINE_HOURS_IN_SECONDS) {
        setStartTime(parseInt(storedStartTime, 10));
        setRemainingTime(NINE_HOURS_IN_SECONDS - elapsedTime);
        setIsPunchedIn(true);
      } else {
        localStorage.removeItem("startTime");
      }
    }
    if (storedPunchInTime) {
      setPunchInTime(
        new Date(parseInt(storedPunchInTime, 10)).toLocaleTimeString([], {
          hour: "numeric",
          minute: "2-digit",
        })
      );
    }
    if (storedPunchOutTime) {
      setPunchOutTime(
        new Date(parseInt(storedPunchOutTime, 10)).toLocaleTimeString([], {
          hour: "numeric",
          minute: "2-digit",
        })
      );
    }
  }, []);

  useEffect(() => {
    let timer;
    if (isPunchedIn) {
      const interval = setInterval(() => {
        setRemainingTime((prevTime) => {
          if (prevTime <= 0) {
            clearInterval(timer);
            setIsPunchedIn(false);
            localStorage.removeItem("startTime");
            return 0;
          }
          return prevTime - 1;
        });
      }, 1000);
      timer = interval;
    }
    return () => clearInterval(timer);
  }, [isPunchedIn]);

  const handleClick = () => {
    if (!isPunchedIn) {
      const currentTime = Date.now();
      localStorage.setItem("startTime", currentTime);
      setStartTime(currentTime);
      localStorage.setItem("punchInTime", currentTime);
      setPunchInTime(
        new Date(currentTime).toLocaleTimeString([], {
          hour: "numeric",
          minute: "2-digit",
        })
      );
    } else {
      const currentTime = Date.now();
      localStorage.setItem("punchOutTime", currentTime);
      setPunchOutTime(
        new Date(currentTime).toLocaleTimeString([], {
          hour: "numeric",
          minute: "2-digit",
        })
      );
      setIsPunchedIn(false);
      localStorage.removeItem("startTime");
      localStorage.removeItem("punchInTime");
      setPunchInTime(null);
    }
    setIsPunchedIn(!isPunchedIn);
  };

  const progressBarWidth =
    ((NINE_HOURS_IN_SECONDS - remainingTime) / NINE_HOURS_IN_SECONDS) * 100;

  const formatTime = (timeInSeconds) => {
    const hours = Math.floor(timeInSeconds / ONE_HOUR_IN_SECONDS);
    const minutes = Math.floor((timeInSeconds % ONE_HOUR_IN_SECONDS) / 60);
    return `${hours}h ${minutes}m`;
  };

  const completedHours = Math.floor(
    (NINE_HOURS_IN_SECONDS - remainingTime) / ONE_HOUR_IN_SECONDS
  );
  const completedMinutes = Math.floor(
    ((NINE_HOURS_IN_SECONDS - remainingTime) % ONE_HOUR_IN_SECONDS) / 60
  );

  return (
    <div>
      <div className="flex justify-between">
        <div className="w-1/2 flex flex-col justify-between">
          <div className="flex flex-col gap-1">
            <h2 className="text-2xl font-bold text-[#5336FD]">
              Good Morning Shubham
            </h2>
            <h2>You have 24 new tasks. It's a lot of work today!</h2>
          </div>
          <Link to="/Task">
            <button className="bg-[#5336FD] px-4 py-2 text-white rounded-md">
              View Tasks
            </button>
          </Link>
        </div>
        <div className="flex flex-col items-end w-1/2">
          <div className="flex items-center gap-2">
            <button
              className={`px-2 py-1.5 rounded-md flex items-center gap-1 text-xs font-bold ${
                isPunchedIn ? "text-red-500" : "text-green-500"
              } text-green-500 bg-sky-100 transition-all duration-700`}
              onClick={handleClick}
            >
              {isPunchedIn ? (
                <>
                  Punch Out <IoLogOut fontSize={20} />
                </>
              ) : (
                <>
                  Punch In <IoLogIn fontSize={20} />
                </>
              )}
            </button>
          </div>
          <div className="w-2/3 mt-20 ">
            <div className="flex justify-between text-xs">
              <span className="flex flex-col items-end">
                {completedHours}h {completedMinutes}m Completed
              </span>
              <span className="flex flex-col items-end">
                {formatTime(remainingTime)} Remaining
              </span>
            </div>
            <div className="bg-sky-100 mt-2 h-5 rounded-full flex justify-start relative overflow-hidden">
              <div
                className="absolute h-full bg-[#5336FD]"
                style={{
                  width: `${progressBarWidth}%`,
                  borderRadius: "inherit",
                }}
              ></div>
            </div>
            <div className="flex justify-between text-xs mt-2">
              <span>In - {punchInTime || "-- : --"}</span>
              <span>Out - {punchOutTime || "-- : --"}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
