import * as React from "react";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { DateCalendar } from "@mui/x-date-pickers/DateCalendar";
import { makeStyles } from "@mui/styles";
import { TiArrowSortedUp } from "react-icons/ti";

// Custom styles
const useStyles = makeStyles({
  root: {
    "& .MuiPickersCalendarHeader-label": {
      fontFamily: "euclid",
      fontSize: 14,
      fontWeight: "bold",
    },
    "& .MuiPickersDay-root": {
      fontFamily: "euclid",
      fontSize: 12,
      fontWeight: "bold",
      borderRadius: 6,
    },
    "& :hover.MuiPickersDay-root": {
      boxShadow: "rgba(0, 0, 0, 0.35) 0px 5px 15px",
      // boxShadow: "rgba(100, 100, 111, 0.2) 0px 7px 29px 0px",
      backgroundColor: "#5336FD",
      color: "white",
    },
    "& :focus.Mui-selected": {
      backgroundColor: "#5336FD",
      color: "white",
    },
    "& .MuiPickersDay-today": {
      // backgroundColor: "#5336FD",
      // color: "white",
      position: "relative",
    },
    "& .MuiPickersDay-today::before": {
      content: "",
      position: "absolute",
      top: "-5px",
      right: "-5px",
      width: "15px",
      height: "15px",
      backgroundColor:
        "red" /* Change this to the desired color for the badge */,
      borderRadius: "50%",
      border: "2px solid white" /* You can customize the border as well */,
    },
    "& :hover.MuiPickersDay-today": {
      backgroundColor: "#5336FD",
      color: "white",
    },
    "& .MuiDayCalendar-weekDayLabel": {
      fontFamily: "euclid",
      fontSize: 13,
      fontWeight: "bold",
      color: "black",
      backgroundColor: "#f0f9ff",
      borderRadius: 6,
      cursor: "pointer",
    },
    display: "block",
    width: "100%",
    fontFamily: "Euclid-medium",
  },
});

export default function Calendar() {
  const classes = useStyles();

  return (
    <div className="flex justify-center sm:justify-end ">
      <LocalizationProvider dateAdapter={AdapterDayjs}>
        <DateCalendar className={`${classes.root} `} />
      </LocalizationProvider>
    </div>
  );
}
