import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { IoLogIn, IoLogOut } from "react-icons/io5";

export default function Greeting() {
  const [isPunchedIn, setIsPunchedIn] = useState(false);
  const [remainingTime, setRemainingTime] = useState(9 * 60 * 60);

  useEffect(() => {
    let timer;
    if (isPunchedIn) {
      timer = setInterval(() => {
        setRemainingTime((prevTime) => prevTime - 1);
      }, 1000);
    } else {
      clearInterval(timer);
    }
    return () => clearInterval(timer);
  }, [isPunchedIn]);

  const handleClick = () => {
    setIsPunchedIn(!isPunchedIn);
  };

  const formatTime = (timeInSeconds) => {
    const hours = Math.floor(timeInSeconds / 3600);
    const minutes = Math.floor((timeInSeconds % 3600) / 60);
    const seconds = timeInSeconds % 60;
    return `${hours}:${minutes.toString().padStart(2, "0")}:${seconds
      .toString()
      .padStart(2, "0")}`;
  };

  return (
    <div>
      <div className="flex justify-between">
        <div className="">
          <div className="flex flex-col gap-1">
            <h2 className="text-2xl font-bold text-[#5336FD]">
              Good Morning Shubham
            </h2>
            <h2>You have 24 new task. Itâ€™s a lots of work today!</h2>
          </div>
          <Link to="/Task">
            <button className="bg-[#5336FD] px-4 py-2 text-white rounded-md">
              View Tasks
            </button>
          </Link>
        </div>

        <div>
          <button
            className={`px-2 py-1.5 rounded-md flex items-center gap-1 text-sm ${
              isPunchedIn ? "bg-red-500" : "bg-green-500"
            } text-white transition-all duration-700`}
            onClick={handleClick}
          >
            {isPunchedIn ? (
              <>
                Punch Out <IoLogOut fontSize={20} />
              </>
            ) : (
              <>
                Punch In <IoLogIn fontSize={20} />
              </>
            )}
            <span>({formatTime(remainingTime)})</span>
          </button>
        </div>
      </div>
    </div>
  );
}
