import React from "react";
import {
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from "@mui/material";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DatePicker } from "@mui/x-date-pickers/DatePicker";
import { DemoContainer } from "@mui/x-date-pickers/internals/demo";
import ArrowDropDownRoundedIcon from "@mui/icons-material/ArrowDropDownRounded";
import PopupMessage from "./PopupMessage";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export function PersonalInformation({ onNext }) {
  // Personal Information Form Component
  return (
    <div className="mt-8">
      <div>
        <h4 className="font-bold">Personal Information</h4>
        <div className="mt-2">
          <div className="flex gap-5 flex-wrap sm:flex-nowrap">
            <div className="w-full lg:w-1/3 flex flex-col">
              <TextField
                className="col-span-12 sm:col-span-6 xl:col-span-2 text-xs"
                id="fname"
                name="fname"
                label="First Name"
                variant="outlined"
                margin="dense"
                sx={{
                  "& .MuiInputLabel-root": {
                    fontSize: 14,
                  },
                  "& .MuiInputBase-root": {
                    backgroundColor: "#EBE9F6",
                    border: "0 none",
                    borderRadius: 2,
                    height: 50,
                  },
                  "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                    borderColor: "transparent",
                  },
                  "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                    {
                      borderColor: "transparent",
                    },
                  "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                    {
                      borderColor: "transparent",
                    },
                }}
              />
            </div>
            <div className="w-full lg:w-1/3 flex flex-col">
              <TextField
                className="col-span-12 sm:col-span-6 xl:col-span-2 text-xs"
                id="lname"
                name="lname"
                label="Last Name"
                variant="outlined"
                margin="dense"
                sx={{
                  "& .MuiInputLabel-root": {
                    fontSize: 14,
                  },
                  "& .MuiInputBase-root": {
                    backgroundColor: "#EBE9F6",
                    border: "0 none",
                    borderRadius: 2,
                    height: 50,
                  },
                  "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                    borderColor: "transparent",
                  },
                  "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                    {
                      borderColor: "transparent",
                    },
                  "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                    {
                      borderColor: "transparent",
                    },
                }}
              />
            </div>
            <div className="w-full lg:w-1/3 flex flex-col">
              <TextField
                className="col-span-12 sm:col-span-6 xl:col-span-2 text-xs"
                id="empid"
                name="empid"
                label="Employee Id"
                variant="outlined"
                margin="dense"
                sx={{
                  "& .MuiInputLabel-root": {
                    fontSize: 14,
                  },
                  "& .MuiInputBase-root": {
                    backgroundColor: "#EBE9F6",
                    border: "0 none",
                    borderRadius: 2,
                    height: 50,
                  },
                  "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                    borderColor: "transparent",
                  },
                  "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                    {
                      borderColor: "transparent",
                    },
                  "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                    {
                      borderColor: "transparent",
                    },
                }}
              />
            </div>
          </div>
          <div className="flex gap-5 flex-wrap sm:flex-nowrap mt-5">
            <div className="w-full lg:w-1/3 flex flex-col">
              <LocalizationProvider
                dateAdapter={AdapterDayjs}
                className="w-full"
              >
                <DemoContainer components={["DatePicker"]}>
                  <DatePicker
                    // defaultValue={dayjs()}
                    // views={["day", "month", "year"]}
                    label="Date of Birth"
                    sx={{
                      "& .MuiInputLabel-root": {
                        fontSize: 14,
                      },
                      "& .MuiInputBase-root": {
                        backgroundColor: "#EBE9F6",
                        border: "0 none",
                        borderRadius: 2,
                        height: 50,
                        width: "100%",
                        overflow: "hidden",
                      },
                      "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                      "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                      "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                        {
                          borderColor: "transparent",
                        },
                      "& ::placeholder": {
                        fontSize: 12,
                      },
                      display: "block", // Ensure DatePicker occupies full width
                      width: "100%", // Ensure DatePicker occupies full width
                    }}
                  />
                </DemoContainer>
              </LocalizationProvider>
            </div>

            <div className="w-full lg:w-1/3 flex flex-col">
              <FormControl
                variant="outlined"
                margin="dense"
                className="col-span-12 sm:col-span-6 xl:col-span-2"
                sx={{
                  "& .MuiInputLabel-root": {
                    fontSize: 14,
                  },
                  "& .MuiInputBase-root": {
                    backgroundColor: "#EBE9F6",
                    border: "0 none",
                    borderRadius: 2,
                    height: 50,
                  },
                  "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                    borderColor: "transparent",
                  },
                  "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                    {
                      borderColor: "transparent",
                    },
                  "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                    {
                      borderColor: "transparent",
                    },
                }}
              >
                <InputLabel id="gender-label" className="w-52">
                  Gender
                </InputLabel>
                <Select
                  labelId="gender-label"
                  id="gender"
                  name="gender"
                  label="Gender"
                  IconComponent={(props) => (
                    <ArrowDropDownRoundedIcon
                      {...props}
                      sx={{ fontSize: 40 }}
                    />
                  )}
                >
                  <MenuItem value="male">Male</MenuItem>
                  <MenuItem value="female">Female</MenuItem>
                </Select>
              </FormControl>
            </div>
            <div className="w-full lg:w-1/3 flex flex-col">
              <FormControl
                variant="outlined"
                margin="dense"
                className="col-span-12 sm:col-span-6 xl:col-span-2"
                sx={{
                  "& .MuiInputLabel-root": {
                    fontSize: 14,
                  },
                  "& .MuiInputBase-root": {
                    backgroundColor: "#EBE9F6",
                    border: "0 none",
                    borderRadius: 2,
                    height: 50,
                  },
                  "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                    borderColor: "transparent",
                  },
                  "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                    {
                      borderColor: "transparent",
                    },
                  "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                    {
                      borderColor: "transparent",
                    },
                }}
              >
                <InputLabel id="gender-label" className="w-52">
                  Gender
                </InputLabel>
                <Select
                  labelId="gender-label"
                  id="gender"
                  name="gender"
                  label="Gender"
                  IconComponent={(props) => (
                    <ArrowDropDownRoundedIcon
                      {...props}
                      sx={{ fontSize: 40 }}
                    />
                  )}
                >
                  <MenuItem value="single">Single</MenuItem>
                  <MenuItem value="married">Married</MenuItem>
                  <MenuItem value="divorced">Divorced</MenuItem>
                  <MenuItem value="widowed">Widowed</MenuItem>
                </Select>
              </FormControl>
            </div>
          </div>
        </div>
      </div>
      <div className="w-full bg-green-500 flex justify-end">
        <button
          type="button"
          onClick={onNext}
          className="bg-blue-600 text-white px-5 py-2 mt-8 rounded-md "
        >
          Next
        </button>
      </div>
    </div>
  );
}

export function EmploymentInformation({ onNext, onPrev }) {
  // Employment Information Form Component
  return (
    <div className="mt-8">
      <h4 className="font-bold">Employment Information</h4>
      <div className="mt-2">
        <div className="flex gap-5 flex-wrap sm:flex-nowrap">
          <div className="w-full lg:w-1/3 flex flex-col">
            <LocalizationProvider dateAdapter={AdapterDayjs} className="w-full">
              <DemoContainer components={["DatePicker"]}>
                <DatePicker
                  // defaultValue={dayjs()}
                  // views={["day", "month", "year"]}
                  label="Date of Joining"
                  sx={{
                    "& .MuiInputLabel-root": {
                      fontSize: 14,
                    },
                    "& .MuiInputBase-root": {
                      backgroundColor: "#EBE9F6",
                      border: "0 none",
                      borderRadius: 2,
                      height: 50,
                      width: "100%",
                      overflow: "hidden",
                    },
                    "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":
                      {
                        borderColor: "transparent",
                      },
                    "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                      {
                        borderColor: "transparent",
                      },
                    "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                      {
                        borderColor: "transparent",
                      },
                    "& ::placeholder": {
                      fontSize: 12,
                    },
                    display: "block", // Ensure DatePicker occupies full width
                    width: "100%", // Ensure DatePicker occupies full width
                  }}
                />
              </DemoContainer>
            </LocalizationProvider>
          </div>

          <div className="w-full lg:w-1/3 flex flex-col">
            <FormControl
              variant="outlined"
              margin="dense"
              className="col-span-12 sm:col-span-6 xl:col-span-2"
              sx={{
                "& .MuiInputLabel-root": {
                  fontSize: 14,
                },
                "& .MuiInputBase-root": {
                  backgroundColor: "#EBE9F6",
                  border: "0 none",
                  borderRadius: 2,
                  height: 50,
                },
                "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline": {
                  borderColor: "transparent",
                },
                "& .MuiOutlinedInput-root:hover .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "transparent",
                  },
                "& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline":
                  {
                    borderColor: "transparent",
                  },
              }}
            >
              <InputLabel id="dept-label" className="w-52">
                Department
              </InputLabel>
              <Select
                labelId="dept-label"
                id="gender"
                name="gender"
                label="Gender"
                IconComponent={(props) => (
