import React from "react";
import { BsPersonFillCheck } from "react-icons/bs";

const PopupMessage = ({ message, onAddMore, onGoToList }) => {
  return (
    <>
      <div
        style={{
          position: "fixed",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          backgroundColor: "white",
          padding: "20px 20px",
          borderRadius: "5px",
          zIndex: "9999",
        }}
      >
        <div className="flex items-center justify-center bg-[#EBE9F6] rounded-md px-4 py-2">
          <BsPersonFillCheck className="text-green-600 text-3xl" />
          <p className="ml-2">{message}</p>
        </div>

        <div className="flex items-center justify-end  mt-5">
          <button
            className="bg-blue-500 text-white px-4 py-2 rounded-md cursor-pointer font-bold hover:bg-blue-600"
            onClick={onAddMore}
          >
            Add More
          </button>
          <button
            className="bg-green-500 text-white px-4 py-2 rounded-md cursor-pointer font-bold hover:bg-green-600"
            onClick={onGoToList}
          >
            Go to List
          </button>
        </div>
      </div>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.5)", // Adjust the opacity as needed
          backdropFilter: "blur(5px)", // Apply the blur effect
          zIndex: "9998",
        }}
      >
        {/* <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            display: "flex",
            flexDirection: "column",
            gap: "10px",
          }}
        ></div> */}
      </div>
    </>
  );
};

export default PopupMessage;
