import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function ViewEmployee() {
  const { empid } = useParams();
  const [employee, setEmployee] = useState(!null);

  useEffect(() => {
    fetchEmployee(empid);
  }, [empid]);

  const fetchEmployee = async (empid) => {
    try {
      const response = await fetch(`/api/employees/${empid}`);
      const data = await response.json();
      setEmployee(data);
    } catch (error) {
      console.error("Error fetching employee details:", error);
    }
  };

  return (
    <div>
      <h2>Employee Details</h2>
      <p>Employee ID: {employee ? employee.empid : "Loading..."}</p>
      <p>Name: {employee ? employee.ename : "Loading..."}</p>
      <p>Designation: {employee ? employee.designation : "Loading..."}</p>
      <p>Joining Date: {employee ? employee.jdate : "Loading..."}</p>
      <p>Status: {employee ? employee.status : "Loading..."}</p>
      {/* Add more details as needed */}
    </div>
  );
}

export default ViewEmployee;
