import React, { useEffect, useState } from "react";
import { BsPersonFillCheck, BsPersonFillAdd } from "react-icons/bs";
import { FaListUl } from "react-icons/fa6";

const PopupMessage = ({ message, onAddMore, onGoToList }) => {
  const [timer, setTimer] = useState(10);
  let countdown;

  useEffect(() => {
    const countdown = setInterval(() => {
      setTimer((prevTimer) => prevTimer - 1);
    }, 1000);

    return () => clearInterval(countdown);
  }, []);

  useEffect(() => {
    if (timer === 0) {
      clearInterval(countdown);
    }
  }, [timer]);

  return (
    <>
      <div
      className="flex flex-col gap-3 py-4"
        style={{
          position: "fixed",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          backgroundColor: "white",
          padding: "25px 20px",
          borderRadius: "5px",
          zIndex: "9999",
        }}
      >
        <div className="flex items-center justify-center bg-[#EBE9F6] rounded-md px-4 py-3">
          <BsPersonFillCheck className="text-green-500 text-3xl" />
          <p className="ml-2">{message}</p>
        </div>

        <div className="flex items-center justify-between mx-8 mt-5">
          <button
            className="bg-[#5336FD] text-white text-sm px-3 py-2 rounded-md cursor-pointer font-bold hover:scale-[1.020] flex items-center"
            onClick={onAddMore}
          >
            <BsPersonFillAdd className="mr-2.5 text-lg" />
            Add More
          </button>
          <button
            className="bg-green-600 text-white text-sm px-3 py-2 rounded-md cursor-pointer font-bold hover:scale-[1.020] flex items-center ml-4"
            onClick={onGoToList}
          >
            <FaListUl className="mr-2.5 text-lg" />
            Go to List
          </button>
        </div>

        <div className="bg-[#EBE9F6]">
          {timer > 0 && (
            <div
              style={{
                top: "10px",
                right: "10px",
                backgroundColor: "white",
                padding: "5px 10px",
                borderRadius: "5px",
                zIndex: "9999",
              }}
            >
              <h1 className="fixed text-sm text-red-500">You Will Ridirect To Employe list page in {timer}s</h1>
            </div>
          )}
        </div>

      </div>
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.5)", // Adjust the opacity as needed
          backdropFilter: "blur(5px)", // Apply the blur effect
          zIndex: "9998",
        }}
        // onClick={onAddMore}
      ></div>
    </>
  );
};

export default PopupMessage;
