import React, { useState, useEffect, useRef } from "react";
import { DASHBOARD_SIDEBAR_LINKS } from "../../lib/consts/navigation";
import logo from "../../assets/images/invezza-logo.png";
import { Link, useLocation } from "react-router-dom";
import classNames from "classnames";
import { TbLayoutSidebarLeftCollapseFilled } from "react-icons/tb";
import { CgMenuGridO } from "react-icons/cg";

const LinkClasses =
  "flex hover:bg-[#EBE9F6] hover:duration-500 p-3 mt-1.5 rounded-md euclid";

export default function Sidebar() {
  const { pathname } = useLocation();
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const sidebarRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (sidebarRef.current && !sidebarRef.current.contains(event.target)) {
        setIsSidebarOpen(false);
      }
    }

    function handleEscapeKey(event) {
      if (event.key === "Escape") {
        setIsSidebarOpen(false);
      }
    }

    document.addEventListener("mousedown", handleClickOutside);
    document.addEventListener("keydown", handleEscapeKey);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
      document.removeEventListener("keydown", handleEscapeKey);
    };
  }, []);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleItemClick = (item) => {
    if (selectedItem === item) {
      setSelectedItem(null); // Deselect if already selected
    } else {
      setSelectedItem(item);
    }
  };

  return (
    <div>
      <button
        className="md:hidden fixed top-2 left-2 p-1.5 text-black bg-white rounded-md"
        onClick={toggleSidebar}
      >
        <CgMenuGridO className="text-4xl " />
      </button>
      <div
        ref={sidebarRef}
        className={`md:flex md:flex-col w-60 h-[97.5vh] md:h-screen  ml-2  mt-2 md:m-0 md:rounded-none rounded-md absolute md:relative p-4 bg-white z-50 shadow-2xl md:shadow-none ${
          isSidebarOpen ? "" : "hidden"
        }`}
      >
        <div className=" flex flex-row justify-between items-center">
          <img src={logo} className="pr-16" alt="logo" />
          <TbLayoutSidebarLeftCollapseFilled
            className="text-2xl absolute flex md:hidden right-4 mt-3 hover:bg-[#EBE9F6] rounded-md"
            onClick={toggleSidebar}
          />
        </div>
        <div className="flex-1 mt-10 mb-2">
          {DASHBOARD_SIDEBAR_LINKS.map((item) => (
            <React.Fragment key={item.key}>
              <div className="relative">
                <SidebarLink
                  item={item}
                  pathname={pathname}
                  onClick={toggleSidebar}
                  onItemClick={handleItemClick}
                  isSelected={selectedItem === item}
                />
                {selectedItem === item && item.subItems && (
                  <div className="top-0 ml-2 bg-white shadow-md rounded-md p-2 border">
                    {item.subItems.map((subItem) => (
                      <SidebarLink
                        key={subItem.key}
                        item={subItem}
                        pathname={pathname}
                        onClick={toggleSidebar}
                      />
                    ))}
                  </div>
                )}
              </div>
            </React.Fragment>
          ))}
        </div>
      </div>
    </div>
  );
}

function SidebarLink({ item, pathname, onClick, onItemClick, isSelected }) {
  return (
    <div
      onClick={() => onItemClick(item)}
      className={classNames(
        "cursor-pointer",
        isSelected
          ? "bg-[#EBE9F6] euclid-bold after:w-1.5 after:bg-[#3C5EFE] after:rounded-full"
          : "",
        LinkClasses
      )}
    >
      <Link to={item.path} className="flex items-center">
        <span className="text-xl">{item.icon}</span>
        <span className="ml-3 w-[90%]">{item.label}</span>
      </Link>
    </div>
  );
}
