import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { IoLogIn, IoLogOut } from "react-icons/io5";

const ONE_HOUR_IN_SECONDS = 3600;
const NINE_HOURS_IN_SECONDS = 9 * ONE_HOUR_IN_SECONDS;

export default function Greeting() {
  const [isPunchedIn, setIsPunchedIn] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const [elapsedTime, setElapsedTime] = useState(0);

  useEffect(() => {
    const storedStartTime = localStorage.getItem("startTime");
    if (storedStartTime) {
      setStartTime(parseInt(storedStartTime, 10));
      const now = Date.now();
      const storedElapsedTime = Math.floor(
        (now - parseInt(storedStartTime, 10)) / 1000
      );
      setElapsedTime(storedElapsedTime);
      setIsPunchedIn(true);
    }
  }, []);

  useEffect(() => {
    let timer;
    if (isPunchedIn) {
      localStorage.setItem("startTime", Date.now());
      timer = setInterval(() => {
        setElapsedTime((prevTime) => prevTime + 1);
      }, 1000);
    } else {
      clearInterval(timer);
      localStorage.removeItem("startTime");
    }
    return () => clearInterval(timer);
  }, [isPunchedIn]);

  const handleClick = () => {
    setIsPunchedIn(!isPunchedIn);
  };

  const remainingTime = isPunchedIn ? NINE_HOURS_IN_SECONDS - elapsedTime : 0;
  const remainingPercentage = Math.max(
    0,
    (remainingTime / NINE_HOURS_IN_SECONDS) * 100
  );

  const formatTime = (timeInSeconds) => {
    const hours = Math.floor(timeInSeconds / ONE_HOUR_IN_SECONDS);
    const minutes = Math.floor((timeInSeconds % ONE_HOUR_IN_SECONDS) / 60);
    const seconds = timeInSeconds % 60;
    return `${hours}:${minutes.toString().padStart(2, "0")}:${seconds
      .toString()
      .padStart(2, "0")}`;
  };
  return (
    <div>
      <div className="flex justify-between">
        <div className="w-1/2 flex flex-col justify-between">
          <div className="flex flex-col gap-1">
            <h2 className="text-2xl font-bold text-[#5336FD]">
              Good Morning Shubham
            </h2>
            <h2>You have 24 new task. Itâ€™s a lots of work today!</h2>
          </div>
          <Link to="/Task">
            <button className="bg-[#5336FD] px-4 py-2 text-white rounded-md">
              View Tasks
            </button>
          </Link>
        </div>

        <div className="flex flex-col items-end w-1/2">
          <div className="flex items-center gap-2">
            <button
              className={`px-2 py-1.5 rounded-md flex items-center gap-1 text-sm ${
                isPunchedIn ? "text-red-500" : "text-green-500"
              } text-green-500 bg-sky-100 transition-all duration-700`}
              onClick={handleClick}
            >
              {isPunchedIn ? (
                <>
                  Punch Out <IoLogOut fontSize={20} />
                </>
              ) : (
                <>
                  Punch In <IoLogIn fontSize={20} />
                </>
              )}
            </button>
          </div>
          <div className="bg-sky-100 w-2/3 mt-20 h-5 rounded-full flex justify-start">
            <span className="-mt-7">({formatTime(remainingTime)})</span>
            <div style={progressBarStyle} className=""></div>
          </div>
        </div>
      </div>
    </div>
  );
}
